import React, { useEffect } from "react";
import MaterialTable from "material-table";
import { makeStyles } from "@material-ui/core/styles";
import { useHistory, Link } from "react-router-dom";

import { useTablesContext } from "../context/tables_context";

const columns = [
  {
    title: "Name",
    field: "name",
  },
  {
    title: "Relationship",
    field: "relationshio",
  },
  {
    title: "BIrth Date",
    field: "birth_date",
    type: "date",
  },
  {
    title: "Phone",
    field: "phone",
  },
  { title: "Age", field: "age", type: "numeric" },
];

export default function UpdateBatchFamily(linkid) {
  let history = useHistory();
  const classes = useStyles();
  const {
    loadSingleBatchFamily,
    singlebatchfamily,
    addFamily,
    deleteFamily,
    updateFamily,
    singlebatchfamily_loading,
    singlebatchfamily_error,
  } = useTablesContext();

  useEffect(() => {
    loadSingleBatchFamily(linkid);
  }, []);

  const update_family = (data) => {
    updateFaimly({ id: data.id, ...data });
    loadSingleBatchFamily();
  };

  const add_family = (data, linkid) => {
    addFamily({ ...data, linkid: linkid });
    loadSingleBatchFamily();
  };

  const delete_Family = (data) => {
    const { id } = data;
    deleteFamily(id);
    loadSingleBatchFamily();
  };

  if (singlebatchfamily_loading) {
    return <div>Loading...Family</div>;
  }
  return (
    <div className={classes.root}>
      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={singlebatchfamily}
          title="Family"
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  add_Family(newData, linkid);
                  resolve();
                }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  update_Family(newData);
                  resolve();
                }, 1000);
              }),
            onRowDelete: (oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  delete_Family(oldData);
                  resolve();
                }, 1000);
              }),
          }}
        />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
