import { selector } from "recoil";

import { departments_url } from "";

export const fetchDepartmentsSelector = selector({
  key: "departmentsSelector",
  get: async ({ get }) => {
    try {
      const res = await fetch(departments_url);
      const data = await res.json();

      return data;
    } catch (error) {
      throw error;
    }
  },
});
