                    })
                    .map((row) => {
                      return (
                        <MenuItem
                          key={row.id}
                          value={row.name}
                          style={{ width: 200 }}
                        >
                          {row.name}
                        </MenuItem>
                      );
                    })}
                  <ListSubheader>Allowances</ListSubheader>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type == "Allowances";
                    })
                    .map((row) => {
                      return (
                        <MenuItem key={row.id} value={row.name}>
                          {row.name}
                        </MenuItem>
                      );
                    })}
                  <ListSubheader>Deductions</ListSubheader>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type == "Deductions";
                    })
                    .map((row) => {
                      return (
                        <MenuItem key={row.id} value={row.name}>
                          {row.name}
                        </MenuItem>
                      );
                    })}
                </TextField>
                ;
              </ul>
            </Grid>
          </form>
          {/* amount column */}
          <Grid
            item
            sm={1}
            //align="right"
            style={{ border: "1px solid white" }}
          >
            <ul>
              <TextField
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={payslipsdata.pay[value][0].payitem1amt}
              />
              <TextField
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={payslipsdata.pay[value][0].payitem2amt}
              />
              <TextField
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={payslipsdata.pay[value][0].payitem3amt}
              />
              <TextField
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={payslipsdata.pay[value][0].payitem4amt}
              />
              <TextField
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={payslipsdata.pay[value][0].payitem5amt}
              />
              <TextField
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={payslipsdata.pay[value][0].payitem6amt}
              />
              <TextField
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={payslipsdata.pay[value][0].payitem7amt}
              />
              <TextField
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={payslipsdata.pay[value][0].payitem8amt}
              />
            </ul>
          </Grid>
          <Grid
            container
            sm={3}
            direction="column"
            style={{ border: "1px solid white" }}
          >
            <h3>Pay Run: {payrundata.payrun}</h3>
            <h3>Pay Period: {payrundata.period}</h3>
            <h3>Name: {payslipsdata.pay[value][0].empname}</h3>
            <Divider variant="fullWidth" className={classes.divider} />
            <h3>Wages: {payslipsdata.pay[value][0].wages}</h3>
            <h3>TAP Amount: {payslipsdata.pay[value][0].tapamount}</h3>
            <h3>SCP Amount: {payslipsdata.pay[value][0].scpamount}</h3>
            <h3>Allowances: {payslipsdata.pay[value][0].allowances}</h3>
            <h3>Deductions: {payslipsdata.pay[value][0].deductions}</h3>
            <Divider variant="fullWidth" className={classes.divider} />
            <h3>Nett Pay:</h3>
          </Grid>
        </Grid>
      </section>
    </Paper>
  );
};

