import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { differenceInDays, addDays } from "date-fns";
import clsx from "clsx";
import {
  Button,
  Paper,
  Grid,
  Icon,
  Divider,
  TextField,
  NativeSelect,
  InputLabel,
  MenuItem,
} from "@material-ui/core";
import { useRecoilState } from "recoil";
import { siteallowsState, loginLevelState } from "./data/atomdata";
import { useDailyAllowancesContext } from "../context/dailyallowances_context";

//const drawerWidth = 240;
const selectYear = [{ name: "2021" }, { name: "2022" }];
const selectMonth = [
  { name: "January" },
  { name: "February" },
  { name: "March" },
  { name: "April" },
  { name: "May" },
  { name: "June" },
];

const DailyAllowancesAddPeriod = ({ handleDialogClose }) => {
  let date = new Date();
  let longMonth = date.toLocaleString("en-us", { month: "long" });
  //console.log("date", date, longMonth);
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [input, setInput] = useRecoilState(siteallowsState);
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const [allowsPeriod, setAllowsPeriod] = useState("");
  const [alert, setAlert] = useState(false);
  const [error, setError] = useState(false);
  const {
    single_dailyallowance,
    updateDailyAllowance,
    addDailyAllowance,
    addDailyAllowsDetl,
    editDailyAllowanceID,
    loadDailyAllowances,
    single_dailyallowance_loading,
    dailyallowance_period,
  } = useDailyAllowancesContext();
  // function daysInMonth(month, year) {
  //   return new Date(year, month, 0).getDate();
  // }

  useEffect(() => {
    setInput({
      ...input,
      empid: loginLevel.loginUserId,
      name: loginLevel.loginUser,
      jobbonus: loginLevel.siteallows_fee,
      perdiem: loginLevel.perdiem_fee,
    });
  }, []);

  const buildSiteAllows = () => {
    const period =
      input.fromdate.substring(0, 4) + "-" + input.fromdate.substring(5, 7);
    // build siteallowsbatch
    addDailyAllowance({
      period: period,
      location: input.location,
      manager: input.manager,
      name: loginLevel.loginUser,
      empid: loginLevel.loginUserId,
      status: "Pending",
    });
    const diffInDays = differenceInDays(
      new Date(input.todate),
      new Date(input.fromdate)
    );
    
    for (let i = 0; i < diffInDays; i++){
      const data = addDays(new Date(), 10);
    }
    //build allows details
    // addDailyAllowsDetl({

    //   name: single_dailyallowance.name,
    //   empid: single_dailyallowance.empid,
    //   period: dailyallowance_period,
    // });
    //handleDialogClose();
  };

  const handleChange = (e) => {
    setInput({ ...input, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const period =
      input.fromdate.substring(0, 4) + "-" + input.fromdate.substring(5, 7);
    setInput({
      ...input,
      period: period,
    });

    buildSiteAllows();
  };

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <form onSubmit={(e) => handleSubmit(e)}>
        <Grid
          container
          spacing={0}
          direction="row"
          alignItems="center"
          justify="center"
          style={{ border: "1px solid white" }}
        >
          <h2>Build Site Allowances</h2>
        </Grid>
        <Grid
          container
          spacing={0}
          direction="row"
          alignItems="center"
          justify="center"
          style={{ border: "1px solid white" }}
        >
          <div>
            <Grid
              item
              sm={12}
              style={{ border: "1px solid white" }}
              direction="column"
              align="left"
            >
              {/* <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Year"
                  id="margin-normal"
                  name="year"
                  required
                  defaultValue={input.year}
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                  style={{ width: "100%", marginTop: 10, marginLeft: 10 }}
                  error={!!error}
                  helperText={error ? error.message : null}
                  select
                >
                  {selectYear.map((item) => {
                    return <MenuItem value={item.name}>{item.name}</MenuItem>;
                  })}
                </TextField>
                <TextField
                  label="Month"
                  id="margin-normal"
                  name="month"
                  required
                  defaultValue={input.month}
                  style={{ width: "100%", marginTop: 10, marginLeft: 0 }}
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                  error={!!error}
                  helperText={error ? error.message : null}
                  select
                >
                  {selectMonth.map((item) => {
                    return <MenuItem value={item.name}>{item.name}</MenuItem>;
                  })}
                </TextField>
              </div> */}
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="From Data"
                  variant="filled"
                  required
                  style={{ width: "100%" }}
                  name="fromdate"
                  value={input.fromdate}
                  type="date"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                ></TextField>
                <TextField
                  label="To Data"
                  variant="filled"
                  required
                  style={{ width: "100%" }}
                  name="todate"
                  value={input.todate}
                  type="date"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                {/* <TextField
                  label="Period"
                  variant="filled"
                  required
                  style={{ width: "100%" }}
                  name="period"
                  value={input.period}
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField> */}
                {/* <Divider className={classes.divider} /> */}
                <TextField
                  label="Manager"
                  variant="filled"
                  required
                  style={{ width: "100%" }}
                  name="manager"
                  value={input.manager}
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="District"
                  variant="filled"
                  required
                  value={input.district}
                  style={{ width: "100%" }}
                  name="district"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
                <TextField
                  label="Location/Rig"
                  variant="filled"
                  required
                  value={input.location}
                  style={{ width: "100%" }}
                  name="location"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Operation Type"
                  variant="filled"
                  required
                  value={input.typeoperation}
                  style={{ width: "100%" }}
                  name="typeoperation"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
                <TextField
                  label="Client"
                  variant="filled"
                  required
                  value={input.client}
                  style={{ width: "100%" }}
                  name="client"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Ticket No/Job No"
                  variant="filled"
                  required
                  value={input.jobno}
                  style={{ width: "100%" }}
                  name="jobno"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
                <TextField
                  label="Crew Operation"
                  variant="filled"
                  required
                  value={input.creqoperation}
                  style={{ width: "100%" }}
                  name="crewoperation"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Job Bonus"
                  variant="filled"
                  required
                  value={input.jobbonus}
                  style={{ width: "100%" }}
                  name="jobbonus"
                  type="currency"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                ></TextField>
                <TextField
                  label="Per Diem"
                  variant="filled"
                  required
                  value={input.perdiem}
                  style={{ width: "100%" }}
                  name="perdiem"
                  type="currency"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                ></TextField>
              </div>
            </Grid>
          </div>
        </Grid>
        <Grid
          container
          spacing={0}
          direction="row"
          alignItems="center"
          justify="center"
          style={{ border: "1px solid white" }}
        >
          <Button
            type="submit"
            variant="contained"
            color="primary"
            className={classes.button}
          >
            Build <Icon className={classes.rightIcon}>send</Icon>
          </Button>
        </Grid>
      </form>
    </Paper>
  );
};

