import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import { makeStyles } from "@material-ui/core/styles";
import { useHistory, Link } from "react-router-dom";
import { useEmployeesContext } from "../context/employees_context";
import { useTablesContext } from "../context/tables_context";

const columns = [
  {
    title: "Name",
    field: "name",
  },
  {
    title: "Relationship",
    field: "relationship",
  },
  {
    title: "BIrth Date",
    field: "birth_date",
    type: "date",
  },
  {
    title: "Phone",
    field: "phone",
  },
  { title: "Age", field: "age", type: "numeric" },
];

export default function Emp_Family({ familydata, setFamilydata }) {
  let history = useHistory();
  const classes = useStyles();
  
  const { editEmployeeID } = useEmployeesContext();
  const {
    loadSingleBatchFamily,
    loadFamily,
    singlebatchfamily,
    addFamily,
    deleteFamily,
    updateFamily,
    singlebatch_family_loading,
    singlebatch_family_error,
  } = useTablesContext();

 
  const update_Family = (data) => {
    const { id, rec_id, tableData, ...fields } = data;
    setTimeout(() => {}, 1000);

    updateFamily({ id, ...fields });
    //loadSingleBatchFamily(editEmployeeID);
    //loadSingleBatchFamily(editEmployeeID);
  };

  const add_Family = (data) => {
    addFamily({ ...data, empid: editEmployeeID });
    //loadSingleBatchFamily(editEmployeeID);
  };

  const delete_Family = (data) => {
    const { id } = data;
    deleteFamily(id);
    //loadSingleBatchFamily(editEmployeeID);
  };

  if (singlebatch_family_loading) {
    return (
      <div>
        <h2>Loading...Family</h2>
      </div>
    );
  }
  return (
    <div className={classes.root}>
      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={familydata}
          title="Family"
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                 setTimeout(() => {
                   setFamilydata([...familydata, newData]);
                   resolve();
                 }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
              const dataUpdate = [...data];
              const index = oldData.tableData.id;
              dataUpdate[index] = newData;
              setData([...dataUpdate]);

              resolve();
            }, 1000)
              }),
            onRowDelete: (oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  delete_Family(oldData);
                  resolve();
                }, 1000);
              }),
          }}
          options={{
            filtering: true,
            headerStyle: {
              backgroundColor: "orange",
              color: "primary",
            },
            showTitle: true,
          }}
        />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
