import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import MaterialTable from "material-table";
import EditableList from "react-list-editable";
import "react-list-editable/lib/react-list-editable.css";
import clsx from "clsx";
import axios from "axios";
import { useAsync } from "react-async";
import {
  Checkbox,
  Paper,
  Grid,
  Divider,
  TextField,
  MenuItem,
  ListSubheader,
} from "@material-ui/core";
import { employees_url } from "../utils/constants";
import { useEmployeesContext } from "../context/employees_context";
import { usePayslipsContext } from "../context/payslips_context";
import { useTablesContext } from "../context/tables_context";
import { payrunState, paydataState } from "./data/atomdata";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";

const drawerWidth = 240;
const url = "https://course-api.com/react-tabs-project";

// const loadEmp = async () =>
//   await fetch(employees_url)
//     .then((res) => (res.ok ? res : Promise.reject(res)))
//     .then((res) => res.json());

// const loadEmp = async () => {
//   const { data } = await axios.get(employees_url);
//   return data;
// };

const paydata_initial = [
  {
    empid: "",
    empname: "",
    wages: 0,
    allowances: 0,
    deductions: 0,
    netpay: 0,
  },
];

const Payrunbatch = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const { loadEmployees, employees, employees_loading } = useEmployeesContext();
  const {
    getPayslipitems,
    payslipitems,
    payslipitems_loading,
    payslipsdata,
    setPayslipsData,
    resetPayslipsData,
  } = usePayslipsContext();
  const { loadPayitems, payitems, payitems_loading } = useTablesContext();
  //const [payitemsdata, setPayitemsdata] = useRecoilState(paydataState);
  const payrundata = useRecoilValue(payrunState);
  const [loadPaydata, setLoadPaydata] = useState(true);
  const [jobs, setJobs] = useState([]);
  const [value, setValue] = useState(0);

  const handleClick = () => {};

  const handleButtonClick = (item, index) => {
    const { id, name, basic_pay } = item;
    const newData = {
      empid: id,
      name: name,
      basic_pay: basic_pay,
      rowindex: index,
    };
    const promise = new Promise((resolve, reject) => {
      setTimeout(() => {
        update_Paydata(newData);
        resolve();
      }, 1000);
    });

    console.log("addpaydata", payslipsdata);
  };

  useEffect(() => {
    loadPayitems();
  }, []);

  const build_Paydata = () => {
    console.log("payslipsdata", payslipsdata);
  };

  const update_Paydata = (newData) => {
    setPayslipsData([...payslipsdata, newData]);
  };

  const onListChange = (newList) => {
    setPayitemsData(newList);
    console.log("list", payitemsdata, newList);
  };

  if (payitems_loading) {
    <div>
      <h2>Loading...payitems</h2>
    </div>;
  }

  if (!paydata) {
    <div>
      <h2>Loading...</h2>
    </div>;
  }
  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
        <Grid
          direction="row"
          container
          spacing={1}
          style={{ border: "1px solid white" }}
        >
          <Grid
            item
            sm={2}
            direction="column"
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h3>Employees</h3>
          </Grid>
          <Grid
            item
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h3>Pay Items</h3>
          </Grid>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h3>Amount</h3>
          </Grid>
          <Grid
            item
            sm={4}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h3>Pay Details</h3>
          </Grid>
        </Grid>
        <Grid
          direction="row"
          container
          spacing={1}
          style={{ border: "1px solid white" }}
        >
          <Grid item sm={2} align="left" style={{ border: "1px solid white" }}>
            {/* {paydata.pay.map((item, index) => {
              //console.log("item", item, index);
              return (
                <button
                  key={index}
                  onClick={(e) => {
                    setValue(index);
                    handleButtonClick(item, index);
                  }}
                  className={`classes.jobbtn`}
                  // ${index === value && "activebtn"} `}
                >
                  <h3>{item.empname}</h3>
                </button>
              );
            })} */}
            <button type="submit" onClick={build_Paydata}>
              Build
            </button>
          </Grid>

          <Grid item sm={3} align="left" style={{ border: "1px solid white" }}>
            <form>
              <ul>
                {/* {paydata.pay.payitems.map((row, index) => {
                  return (
                    <TextField
                      variant="filled"
                      type="text"
                      style={{ width: 200 }}
                      value={row.item}
                      select
                    >
                      <ListSubheader>Wages</ListSubheader>
                      {payitems
                        .filter(function (item) {
                          return item.pay_type == "Wages";
                        })
                        .map((row) => {
                          return (
                            <MenuItem key={row.id} value={row.name}>
                              {row.name}
                            </MenuItem>
                          );
                        })}
                      <ListSubheader>Allowances</ListSubheader>
                      {payitems
                        .filter(function (item) {
                          return item.pay_type == "Allowances";
                        })
                        .map((row) => {
                          return (
                            <MenuItem key={row.id} value={row.name}>
                              {row.name}
                            </MenuItem>
                          );
                        })}
                      <ListSubheader>Deductions</ListSubheader>
                      {payitems
                        .filter(function (item) {
                          return item.pay_type == "Deductions";
                        })
                        .map((row) => {
                          return (
                            <MenuItem key={row.id} value={row.name}>
                              {row.name}
                            </MenuItem>
                          );
                        })}
                    </TextField>
                  );
                })} */}
              </ul>
            </form>
          </Grid>
          <Grid
            item
            sm={2}
            //align="right"
            style={{ border: "1px solid white" }}
          >
            <ul>
              {/* {paydata.pay.payitems.map((row, index) => {
                return (
                  <TextField
                    variant="filled"
                    type="currency"
                    style={{ width: 100 }}
                    value={row.amt}
                  />
                );
              })} */}
            </ul>
          </Grid>
          <Grid
            container
            sm={4}
            direction="column"
            style={{ border: "1px solid white" }}
          >
            <h3>Pay Run: {payrundata.payrun}</h3>
            <h3>Pay Period: {payrundata.period}</h3>
            <h3>Name:</h3>
            <Divider variant="fullWidth" className={classes.divider} />
            <h3>Wages:</h3>
            <h3>TAP Amount:</h3>
            <h3>SCP Amount:</h3>
            <h3>Allowances:</h3>
            <h3>Deductions:</h3>
            <Divider variant="fullWidth" className={classes.divider} />
            <h3>Nett Pay:</h3>
          </Grid>
        </Grid>
      </section>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "70vw",
    margin: "5rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
