import React, { useState } from "react";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";

import Login from "./Login";
import DashboardMain from "./DashboardMain";
import DashboardStaff from "./DashboardStaff";
import DashboardAdmin from "./DashboardAdmin";

const Main = () => {
  const [login, setLogin] = React.useState(false);
  const [loginLevel, setLoginLevel] = useState("");

    const SwitchCase = ({value}) => {
      console.log(value)
    switch (value) {
      case "Staff":
        return <DashboardStaff />;
      case "Admin":
        return <DashboardAdmin />;
      case "AdminManager":
        return <DashboardMain />;
      case "Manager":
        return <DashboardMain />;
      default:
        return "You are not authorised user!";
    }
  };

  if (!login) {
    return <Login setLogin={setLogin} setLoginLevel={setLoginLevel} />;
  }
  return (
    <div>
      <SwitchCase value={loginLevel} />
    </div>
  );
};

export default Main;
