import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import MaterialTable from "material-table";
import EditableList from "react-list-editable";
import "react-list-editable/lib/react-list-editable.css";
import clsx from "clsx";
import axios from "axios";
import { useAsync } from "react-async";
import {
  Button,
  Checkbox,
  Paper,
  Grid,
  Divider,
  TextField,
  MenuItem,
  ListSubheader,
} from "@material-ui/core";
import { employees_url } from "../utils/constants";
import { useEmployeesContext } from "../context/employees_context";
import { usePayslipsContext } from "../context/payslips_context";
import { useTablesContext } from "../context/tables_context";
import { payrunState, paydataState } from "./data/atomdata";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";

const drawerWidth = 240;
const url = "https://course-api.com/react-tabs-project";

// const loadEmp = async () =>
//   await fetch(employees_url)
//     .then((res) => (res.ok ? res : Promise.reject(res)))
//     .then((res) => res.json());

// const loadEmp = async () => {
//   const { data } = await axios.get(employees_url);
//   return data;
// };

const paydata_initial = [
  {
    empid: "",
    empname: "",
    wages: 0,
    allowances: 0,
    deductions: 0,
    netpay: 0,
  },
];

const Payrunbatch = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const { loadEmployees, employees, employees_loading } = useEmployeesContext();
  const {
    getPayslipitems,
    payslipitems,
    payslipitems_loading,
    payslipsdata,
    setPayslipsData,
    resetPayslipsData,
  } = usePayslipsContext();
  const { loadPayitems, payitems, payitems_loading } = useTablesContext();
  //const [payitemsdata, setPayitemsdata] = useRecoilState(paydataState);
  const payrundata = useRecoilValue(payrunState);
  const [loadPaydata, setLoadPaydata] = useState(true);
  const [jobs, setJobs] = useState([]);
  const [value, setValue] = useState(0);
  const handleClick = () => {};
  let i = -1;
  console.log("payrunbatch", payslipsdata);

  const handleButtonClick = (item, index) => {
    const { id, name, basic_pay } = item;
    const newData = {
      empid: id,
      name: name,
      basic_pay: basic_pay,
      rowindex: index,
    };
    const promise = new Promise((resolve, reject) => {
      setTimeout(() => {
        update_Paydata(newData);
        resolve();
      }, 1000);
    });

    console.log("addpaydata", payslipsdata);
  };

  useEffect(() => {
    loadPayitems();
  }, []);

  const build_Paydata = () => {
    console.log("payslipsdata", payslipsdata);
  };

  const update_Paydata = (newData) => {
    setPayslipsData([...payslipsdata, newData]);
  };

  const onListChange = (newList) => {
    setPayslipsData(newList);
    console.log("list", payslipsdata, newList);
  };

  const handleChangePayitem = (e) => {
    console.log(e);
  };

  if (payitems_loading) {
    <div>
      <h2>Loading...payitems</h2>
    </div>;
  }

  if (!payslipsdata.pay) {
    <div>
      <h2>Loading...</h2>
    </div>;
  }
  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
        <Grid
          direction="row"
          container
          spacing={1}
          style={{ border: "1px solid white" }}
        >
          <Grid
            item
            xs={6}
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Employees</h2>
          </Grid>
          <Grid
            item
            xs={6}
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Pay Items</h2>
          </Grid>
          <Grid
            item
            xs={6}
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Amount</h2>
          </Grid>
          <Grid
            item
            xs={6}
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Pay Details</h2>
          </Grid>
        </Grid>
        {/* employees column */}
        <Grid
          direction="row"
          container
          spacing={1}
          style={{ border: "1px solid white" }}
        >
          <Grid
            item
            xs={6}
            sm={3}
            align="left"
            style={{ border: "1px solid white" }}
          >
            {payslipsdata.pay.map((items, index) => {
              return items.map((subItems, sIndex) => {
                return (
                  <div>
                    <Button
                      key={index}
                      onClick={(e) => {
                        setValue(index);
                        //   handleButtonClick(subItems, index);
                      }}
                      className={`classes.jobbtn`}
                      // ${index === value && "activebtn"} `}
                    >
                      <h3>{subItems.empname}</h3>
                    </Button>
                  </div>
                );
              });
            })}
          </Grid>
          {/* payitems columns */}
          <Grid
            item
            xs={6}
            sm={3}
            align="left"
            style={{ border: "1px solid white" }}
          >
            <form>
              <ul>
                <div>
                  <TextField
                    variant="filled"
                    type="text"
                    style={{ width: 180 }}
                    value={payslipsdata.pay[value][0].payitem1}
                    select
                  >
                    <ListSubheader>Wages</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Wages";
                      })
                      .map((row) => {
                        return (
                          <MenuItem
                            key={row.id}
                            value={row.name}
                            style={{ width: 180 }}
                          >
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    <ListSubheader>Allowances</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Allowances";
                      })
                      .map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    <ListSubheader>Deductions</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Deductions";
                      })
                      .map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
                            {row.name}
                          </MenuItem>
                        );
                      })}
                  </TextField>
                </div>
                <div>
                  <TextField
                    variant="filled"
                    type="text"
                    style={{ width: 180 }}
                    value={payslipsdata.pay[value][0].payitem2}
                    onChange={(e) => handleChangePayitem(e)}
                    select
                  >
                    <ListSubheader>Wages</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Wages";
                      })
                      .map((row) => {
                        return (
                          <MenuItem
                            key={row.id}
                            value={row.name}
                            style={{ width: 180 }}
                          >
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    <ListSubheader>Allowances</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Allowances";
                      })
                      .map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    <ListSubheader>Deductions</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Deductions";
                      })
                      .map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
                            {row.name}
                          </MenuItem>
                        );
                      })}
                  </TextField>
                </div>
                <div>
                  <TextField
                    variant="filled"
                    type="text"
                    style={{ width: 180 }}
                    value={payslipsdata.pay[value][0].payitem3}
                    select
                  >
                    <ListSubheader>Wages</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Wages";
                      })
                      .map((row) => {
                        return (
                          <MenuItem
                            key={row.id}
                            value={row.name}
                            style={{ width: 180 }}
                          >
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    <ListSubheader>Allowances</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Allowances";
                      })
                      .map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    <ListSubheader>Deductions</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Deductions";
                      })
                      .map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
                            {row.name}
                          </MenuItem>
                        );
                      })}
                  </TextField>
                </div>
                <div>
                  <TextField
                    variant="filled"
                    type="text"
                    style={{ width: 180 }}
                    value={payslipsdata.pay[value][0].payitem4}
                    select
                  >
                    <ListSubheader>Wages</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Wages";
                      })
                      .map((row) => {
                        return (
                          <MenuItem
                            key={row.id}
                            value={row.name}
                            style={{ width: 180 }}
                          >
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    <ListSubheader>Allowances</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Allowances";
                      })
                      .map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    <ListSubheader>Deductions</ListSubheader>
                    {payitems
                      .filter(function (item) {
                        return item.pay_type == "Deductions";
                      })
                      .map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
