import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import axios from "axios";
import MaterialTable from "material-table";
import { Grid, List, ListItem, ListItemText } from "@material-ui/core";
import { selector, useRecoilState } from "recoil";
import { loginLevelState } from "./data/atomdata";
import { expenses_url } from "../utils/constants";

const drawerWidth = 240;

const columns = [
  {
    title: "Name",
    field: "name",
  },
  {
    title: "Date",
    field: "date",
    type: "date",
    dateSetting: { locale: "en-GB" },
  },

  { title: "Description", field: "description" },
  { title: "Amount", field: "amount", type: "currency" },
  { title: "Status", field: "status" },
];




const ExpenseTableViewStaff = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
   const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const [userdata, setUserdata] = useState([]);
  //const [userdata, setUserdata] = useRecoilState(userdatastate);
  const wpExpiryDetails = useRecoilValueLoadable(fetchExpensesDetails);
  const { state, contents } = wpExpiryDetails;


  
  console.log(wpExpiryDetails);
  if (wpExpiryDetails.state === "hasError") {
    return (
      <div>
        <h2>Internet connections problem!</h2>
      </div>
    );
  }

  if (state === "loading") {
    return (
      <div>
        <h2>Loading....Expenses</h2>
      </div>
    );
  }

  if (state === "hasValue") {
    const editable = contents.map((r) => {
      return { ...r };
    });
    return (
      <List className={classes.root}>
        <Grid container direction="row">
          {editable.filter((i)=>i.empid === loginLevel.loginUserId).map((row) => {
            return (
              <ListItem key={row.id}>
                <Grid item sm={2} align="center">
                  <ListItemText>{row.name}</ListItemText>
                </Grid>
                <Grid item sm={2} align="center">
                  <ListItemText>{row.date}</ListItemText>
                </Grid>
                <Grid item sm={4} align="center">
                  <ListItemText>{row.description}</ListItemText>
                </Grid>
                <Grid item sm={2} align="center">
                  <ListItemText>{row.amount}</ListItemText>
                </Grid>
                <Grid item sm={2} align="center">
                  <ListItemText>{row.status}</ListItemText>
                </Grid>
              </ListItem>
            );
          })}
        </Grid>
      </List>
    );
  }
};

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));

export default ExpenseTableViewStaff;
