import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import MaterialTable from "material-table";
import Paper from "@material-ui/core/Paper";
import {
  atom,
  selector,
  useRecoilValue,
  useRecoilState,
  useSetRecoilState,
  useRecoilValueLoadable,
  useRecoilStateLoadable,
} from "recoil";
// import { useRecoilValue, useSetRecoilState, useRecoilState } from "recoil";
// import { departmentsSelector } from "../helpers/Recoilhelpers";
import { departments_url } from "../utils/constants";
import { fetchDepartmentsSelector } from "./data/selectordata";

const url = `https://reqres.in/api/users?page=1`;

const drawerWidth = 240;

const columns = [
  {
    title: "Name",
    field: "name",
    filtering: false,
  },
];

export const userdatastate = atom({
  key: "userdata",
  default: [],
  dangerouslyAllowMutability: true,
});

const fetchUserDetails = selector({
  key: "userDetailsSelector",
  get: async ({ get }) => {
    try {
      const res = await fetch(departments_url);
      const data = await res.json();
      console.log(data);
      return data;
    } catch (error) {
      throw error;
    }
  },
});

const Example = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [userdata, setUserdata] = useRecoilState(userdatastate);
  const [userDetails, setUserDetails] = useRecoilState(
    fetchDepartmentsSelector
  );
  //const { state, contents } = userDetails;

  console.log(userDetails);

  return (
    <paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <React.Suspense fallback={<div>Loading...</div>}>
