import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import MaterialTable from "material-table";
import clsx from "clsx";
import { useHistory } from "react-router-dom";
import moment from "moment";
import { format } from "date-fns";
import axios from "axios";
import { useAsync } from "react-async";
import { Controller, useForm } from "react-hook-form";
import Alert from "@material-ui/lab/Alert";
import {
  Button,
  Paper,
  Grid,
  Icon,
  Divider,
  TextField,
  MenuItem,
} from "@material-ui/core";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";

import { employees_url } from "../utils/constants";
import { useEmployeesContext } from "../context/employees_context";
import { usePayslipsContext } from "../context/payslips_context";
import { payrunState, payPeriodIdState } from "./data/atomdata";

const drawerWidth = 240;
const url = "https://course-api.com/react-tabs-project";

const columns = [
  {
    title: "Period",
    field: "period",
    editable: "never",
    cellStyle: {
      width: 280,
      maxWidth: 280,
    },
  },
  {
    title: "Pay Run",
    field: "payrun",
    editable: "never",
    cellStyle: {
      width: 280,
      maxWidth: 280,
    },
  },
  {
    title: "From Date",
    field: "from_date",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
    cellStyle: {
      width: 150,
      maxWidth: 150,
    },
  },
  {
    title: "To Date",
    field: "to_date",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
  },
  {
    title: "Pay Date",
    field: "pay_date",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
    cellStyle: {
      width: 150,
      maxWidth: 150,
    },
  },
  {
    title: "Status",
    field: "status",
    type: "text",
    editable: "never",
  },
];

// const loadEmp = async () =>
//   await fetch(employees_url)
//     .then((res) => (res.ok ? res : Promise.reject(res)))
//     .then((res) => res.json());

const Payrun = () => {
  let history = useHistory();
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const { loadEmployees, employees } = useEmployeesContext();
  const { addPayrun, getPayrun, payrun, payrun_loading } = usePayslipsContext();
  const [input, setInput] = useRecoilState(payrunState);
  const [alert, setAlert] = useState(false);
  const [error, setError] = useState(false);

  function formatDate(string) {
    var options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(string).toLocaleDateString("en-GB", options);
  }

  function formatPayrun() {
    const yy =
      input.fromdate.substring(0, 4) + "-" + input.fromdate.substring(5, 7);
    const mm = input.fromdate.substring(5, 7);
    const d = input.fromdate;
    console.log("payrun", yy, mm, d);
  }

  // var today = new Date();
  // console.log(yyyymmdd(today));

  const handleChange = (e) => {
    setInput({ ...input, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const period =
      formatDate(input.fromdate) + " - " + formatDate(input.todate);
    const payrun =
      input.fromdate.substring(0, 4) + "-" + input.fromdate.substring(5, 7);
    setInput({ ...input, period: period, payrun: payrun });
    formatPayrun();
  };

  const handleNext = () => {
    //loadEmployees();
    history.push("/payrunbatch");
  };

  const add_Payrun = () => {
    //update payrun
    addPayrun({
      pay_freq: input.payfreq,
      from_date: input.fromdate,
      to_date: input.todate,
      pay_date: input.paydate,
      period: input.period,
      payrun: input.payrun,
    });
    getPayrun();
  };

  useEffect(() => {
    loadEmployees();
    getPayrun();
  }, []);

  useEffect(() => {
    if (input.period && input.payrun) {
      add_Payrun();
      setAlert(true);
    }
  }, [input]);

  if (payrun_loading) {
    <div>
      <h2>Loading....</h2>
    </div>;
  }

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
        <Grid
          direction="row"
          container
          spacing={1}
          style={{ border: "1px solid white" }}
        >
          <Grid container item sm={3} direction="column" align="left">
            <article className={classes.jobinfo}>
              <h2>Pay Run</h2>
              <form onSubmit={handleSubmit}>
                <div>
                  <TextField
                    label="Pay Frequency"
                    variant="filled"
                    required
                    style={{ width: 250 }}
                    name="payfreq"
                    value={input.payfreq}
                    onChange={(e) => handleChange(e)}
                    select
                  >
                    {/* <MenuItem value="Weekly">Weekly</MenuItem> */}
                    <MenuItem value="Monthly">Monthly</MenuItem>
                  </TextField>
                </div>
                <div>
                  <TextField
                    label="From Date"
                    variant="filled"
                    name="fromdate"
                    type="date"
                    value={input.fromdate}
                    required
                    style={{ width: 250 }}
                    onChange={(e) => handleChange(e)}
                    InputLabelProps={{
                      shrink: true,
                    }}
                  />
                </div>
                <div>
                  <TextField
                    label="To Date"
                    variant="filled"
                    type="date"
                    name="todate"
                    value={input.todate}
                    required
                    onChange={(e) => handleChange(e)}
                    style={{ width: 250 }}
                    onChange={handleChange}
                    InputLabelProps={{
                      shrink: true,
                    }}
                  />
                </div>
                <div>
                  <TextField
                    label="Payment Date"
                    variant="filled"
                    type="date"
                    name="paydate"
                    value={input.paydate}
                    required
                    style={{ width: 250 }}
                    onChange={(e) => handleChange(e)}
                    InputLabelProps={{
                      shrink: true,
                    }}
                  />
                </div>
                <div>
                  <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    className={classes.button}
                  >
                    Submit <Icon className={classes.rightIcon}>send</Icon>
                  </Button>
                  {alert && (
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      className={classes.button}
                      onClick={handleNext}
                    >
                      Next <Icon className={classes.rightIcon}>send</Icon>
                    </Button>
                  )}
                </div>
                <div>{alert && <h3>New Payrun being added!</h3>}</div>
                <div>
                  {error && <h3>This Payrun period already existed!</h3>}
                </div>
              </form>
            </article>
          </Grid>
          <Divider
            orientation="vertical"
            flexItem
            style={{ background: "white" }}
          />
          <Grid container item sm={8} align="right">
            {/* <MaterialTable
              columns={columns}
              data={payrun}
              title="Payrun History"
              options={{
                filtering: false,
                search: false,
                toolbar: false,
                headerStyle: {
                  backgroundColor: "orange",
                  color: "primary",
                },
                showTitle: false,
              }}
            /> */}
            <form onSubmit={handleSubmit(onSubmit)}>
              {employees.map((item) => {
                const { name } = item;
                return (
                  <div>
                    <Controller
                      name="name"
                      control={control}
                      defaultValue={name}
                      render={({
                        field: { onChange, value },
                        fieldState: { error },
                      }) => {
                        return (
                          <TextField
                            label="Name"
                            id="margin-normal"
                            name="name"
                            defaultValue={name}
                            className={classes.textField}
                            onChange={onChange}
                            error={!!error}
                            helperText={error ? error.message : null}
                          />
                        );
                      }}
                      rules={{ required: "Name required" }}
                    />
                  </div>
                );
              })}
            </form>
          </Grid>
        </Grid>
      </section>
    </Paper>
  );
};

