import React, { useState, useReducer, useEffect } from "react";
import {
  Button,
  Icon,
  TextField,
  Paper,
  Typography,
  Select,
  Grid,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import { useEmployeesContext } from "../context/employees_context";
import { Controller, useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";

import UpdateBatchFamily from "./UpdateBatchFamily";
import UpdateBatchEducations from "./UpdateBatchEducations";
import UpdateBatchExperiences from "./UpdateBatchExperiences";

const initial_values = {
  name: "",
  gender: "",
  ic_no: "",
  email: "",
  age: 0,
};

const EmployeeForm = () => {
  let history = useHistory();
  const classes = useStyles();
  const {
    isEditing,
    single_employee,
    updateEmployee,
    addEmployee,
    editEmployeeID,
    loadEmployees,
    single_employee_loading,
  } = useEmployeesContext();
  //const { name, ic_no, gender, age, email } = single_employee || initial_values;
  const [state, setState] = useState(initial_values);
  const { name, ic_no, gender, age, email } = state;

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.value });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("user created", state);
  };

  // const onSubmit = (data) => {
  //   if (isEditing) {
  //     updateEmployee({ id: editEmployeeID, ...data });
  //   } else {
  //     addEmployee({ ...data });
  //   }
  //   loadEmployees();
  //   history.push("/allemployees");
  // };

  if (single_employee_loading) {
    return <div>Loading...</div>;
  }
  return (
    <div>
      <Grid xs={12}>
        <Paper className={classes.root}>
          <Typography variant="h5" component="h3">
            EMPLOYEE FORM
          </Typography>
          <Typography component="p">Employee Particulars</Typography>

          <form onSubmit={handleSubmit}>
            <div>
              <TextField
                label="Name"
                id="margin-normal"
                name="name"
                defaultValue={name}
                className={classes.textField}
                onChange={handleChange}
                helperText={error ? error.message : null}
              />
            </div>
            <div>
              <TextField
                label="IC No"
                id="margin-normal"
                name="ic_no"
                defaultValue={ic_no}
                className={classes.textField}
                onChange={onChange}
                helperText={error ? error.message : null}
              />
            </div>
            <div>
              <TextField
                label="Gender"
                id="margin-normal"
                name="gender"
                defaultValue={gender}
                className={classes.textField}
                onChange={onChange}
                error={!!error}
                helperText={error ? error.message : null}
                select
              >
                <MenuItem value="Male">Male</MenuItem>
                <MenuItem value="Female">Female</MenuItem>
              </TextField>
              );
            </div>
            <div>
              <TextField
                label="Age"
                type="number"
                id="standard-number"
                name="age"
                defaultValue={age}
                className={classes.textField}
                //onChange={onChange}
                onChange={(e) => {
                  onChange(parseInt(e.target.value, 10));
                }}
                error={!!error}
                helperText={error ? error.message : null}
              />
            </div>
            <div>
              <TextField
                label="Email"
                id="margin-normal"
                name="email"
                defaultValue={email}
                className={classes.textField}
                onChange={onChange}
                error={!!error}
                helperText={error ? error.message : null}
              />
            </div>

            <div>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
              >
                Submit <Icon className={classes.rightIcon}>send</Icon>
              </Button>
            </div>
          </form>
        </Paper>
      </Grid>
      {/* <Grid xs={12}>
        <UpdateBatchFamily empid={editEmployeeID} />
      </Grid>
      <Grid xs={12}>
        <UpdateBatchEducations empid={editEmployeeID} />
      </Grid>
      <Grid xs={12}>
        <UpdateBatchExperiences empid={editEmployeeID} />
      </Grid> */}
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
  leftIcon: {
    marginRight: theme.spacing(1),
  },
  rightIcon: {
    marginLeft: theme.spacing(1),
  },
  iconSmall: {
    fontSize: 20,
  },
  root: {
    padding: theme.spacing(3, 2),
  },
  container: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 400,
  },
}));

export default EmployeeForm;
