import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import MaterialTable from "material-table";
import { Controller, useForm } from "react-hook-form";
import clsx from "clsx";
import axios from "axios";
import { useAsync } from "react-async";
import {
  Button,
  Checkbox,
  Paper,
  Grid,
  Divider,
  TextField,
  MenuItem,
  ListSubheader,
} from "@material-ui/core";
import { employees_url } from "../utils/constants";
import { useEmployeesContext } from "../context/employees_context";
import {
  usePayslipsContext,
  updatePayslipsData,
} from "../context/payslips_context";
import { useTablesContext } from "../context/tables_context";
import { payrunState, paydataState } from "./data/atomdata";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";

const drawerWidth = 240;
const url = "https://course-api.com/react-tabs-project";

// const loadEmp = async () =>
//   await fetch(employees_url)
//     .then((res) => (res.ok ? res : Promise.reject(res)))
//     .then((res) => res.json());

// const loadEmp = async () => {
//   const { data } = await axios.get(employees_url);
//   return data;
// };

const paydata_initial = [
  {
    empid: "",
    empname: "",
    wages: 0,
    allowances: 0,
    deductions: 0,
    netpay: 0,
  },
];

const Payrunbatch = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const { register, handleSubmit, control } = useForm();
  const { loadEmployees, employees, employees_loading } = useEmployeesContext();
  const {
    getPayslipitems,
    payslipitems,
    payslipitems_loading,
    payslipsdata,
    setPayslipsData,
    resetPayslipsData,
    updatePayslipsData,
  } = usePayslipsContext();
  const { loadPayitems, payitems, payitems_loading } = useTablesContext();
  //const [payitemsdata] = useRecoilState(paydataState);
  const payrundata = useRecoilValue(payrunState);
  const [loadPaydata, setLoadPaydata] = useState(true);
  const [formdata, setFormdata] = useState([]);
  const [rowindex, setRowIndex] = useState(0);
  const handleClick = () => {};
  let i = -1;
  console.log("payrunbatch", payslipsdata);

  const handleButtonClick = (item, index) => {
    const { id, name, basic_pay } = item;
    const newData = {
      empid: id,
      name: name,
      basic_pay: basic_pay,
      rowindex: index,
    };
    const promise = new Promise((resolve, reject) => {
      setTimeout(() => {
        update_Paydata(newData);
        resolve();
      }, 1000);
    });

    console.log("addpaydata", payslipsdata);
  };

  useEffect(() => {
    loadPayitems();
  }, []);

  const build_Paydata = () => {
    console.log("payslipsdata", payslipsdata);
  };

  const update_Paydata = (newData) => {
    setPayslipsData([...payslipsdata, newData]);
  };

  const onListChange = (newList) => {
    setPayslipsData(newList);
    console.log("list", payslipsdata, newList);
  };

  const handleEmpButtonClick = (index) => {
    let items = [];
    setFormdata([]);
    const newData = [
      {
        empid: payslipsdata.pay[index][0].empid,
        empname: payslipsdata.pay[index][0].empnamename,
        basic_salary: payslipsdata.pay[index][0].basic_salary,
        wages: payslipsdata.pay[index][0].wages,
        tapamount: payslipsdata.pay[index][0].tapamount,
        scpamount: payslipsdata.pay[index][0].scpamount,
        allowances: payslipsdata.pay[index][0].allowances,
        deductions: payslipsdata.pay[index][0].deductions,
        nettpay: payslipsdata.pay[index][0].nettpay,
        payitem1: payslipsdata.pay[index][0].payitem1,
        payitem1amt: basic_salary,
        payitem2: payslipsdata.pay[index][0].payitem2,
        payitem2amt: payslipsdata.pay[index][0].payitem2amt,
        payitem3: payslipsdata.pay[index][0].payitem3,
        payitem3amt: payslipsdata.pay[index][0].payitem3amt,
        payitem4: payslipsdata.pay[index][0].payitem4,
        payitem4amt: payslipsdata.pay[index][0].payitem4amt,
        payitem5: payslipsdata.pay[index][0].payitem5,
        payitem5amt: payslipsdata.pay[index][0].payitem5amt,
        payitem6: payslipsdata.pay[index][0].payitem6,
        payitem6amt: payslipsdata.pay[index][0].payitem6amt,
        payitem7: payslipsdata.pay[index][0].payitem7,
        payitem7amt: payslipsdata.pay[index][0].payitem7amt,
        payitem8: payslipsdata.pay[index][0].payitem8,
        payitem8amt: payslipsdata.pay[index][0].payitem8amt,
      },
    ];
      items.push(newData);
      setFormdata
  };

  const onFormSubmit = (data) => {
    console.log(data);
  };

  if (payitems_loading) {
    <div>
      <h2>Loading...payitems</h2>
    </div>;
  }

  if (!payslipsdata.pay) {
    <div>
      <h2>Loading...</h2>
    </div>;
  }
  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
