          direction="row"
          container
          spacing={1}
          style={{ border: "1px solid white" }}
        >
          <Grid
            item
            xs={6}
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Employees</h2>
          </Grid>
          <Grid
            item
            xs={6}
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Pay Items</h2>
          </Grid>
          <Grid
            item
            xs={6}
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Amount</h2>
          </Grid>
          <Grid
            item
            xs={6}
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Pay Details</h2>
          </Grid>
        </Grid>
        {/* employees column */}
        <form onSubmit={handleSubmit(onFormSubmit)}>
          <Grid
            direction="row"
            container
            spacing={1}
            style={{ border: "1px solid white" }}
          >
            <Grid
              item
              xs={6}
              sm={3}
              align="left"
              style={{ border: "1px solid white" }}
            >
              {payslipsdata.pay.map((items, index) => {
                return items.map((subItems, sIndex) => {
                  return (
                    <div>
                      <Button
                        key={index}
                        type="submit"
                        onClick={(e) => {
                          handleEmpButtonClick(index);
                        }}
                        className={`classes.jobbtn`}
                        // ${index === value && "activebtn"} `}
                      >
                        <h3>{subItems.empname}</h3>
                      </Button>
                    </div>
                  );
                });
              })}
            </Grid>

            {/* payitems columns */}
            <Grid
              item
              xs={6}
              sm={3}
              align="left"
              style={{ border: "1px solid white" }}
            >
              <form>
                <ul>
                  <div>
                    <Controller
                      name="payitem1"
                      control={control}
                      defaultValue={payslipsdata.pay[rowindex][0].payitem1}
                      render={({ field: { onChange, value } }) => (
                        <TextField
                          variant="filled"
                          type="text"
                          value={value}
                          style={{ width: 180 }}
                          value={value}
                          onChange={onChange}
                          select
                        >
                          <ListSubheader>Wages</ListSubheader>
                          {payitems
                            .filter(function (item) {
                              return item.pay_type == "Wages";
                            })
                            .map((row) => {
                              return (
                                <MenuItem
                                  key={row.id}
                                  value={row.name}
                                  style={{ width: 180 }}
                                >
                                  {row.name}
                                </MenuItem>
                              );
                            })}
                          <ListSubheader>Allowances</ListSubheader>
                          {payitems
                            .filter(function (item) {
                              return item.pay_type == "Allowances";
                            })
                            .map((row) => {
                              return (
                                <MenuItem key={row.id} value={row.name}>
                                  {row.name}
                                </MenuItem>
                              );
                            })}
                          <ListSubheader>Deductions</ListSubheader>
                          {payitems
