import React, { useState, useEffect } from "react";
import {
  Button,
  ButtonGroup,
  Grid,
  Icon,
  TextField,
  Divider,
  ListSubheader,
  MenuItem,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { singlebatchpayslip } from "../context/payslips_context";
import { usePayslipsContext } from "../context/payslips_context";

const initial_state = [
  {
    name: "",
    period: "",
    date: "",
    payrun: "",
    nett_pay: 0,
    bank_name: "",
    bank_acno: "",
    tap_acno: "",
    tap_amount: 0,
    scp_acno: "",
    scp_amount: 0,
    empid: "",
    status: "",
    allows_type1: " ",
    allows_type1amt: 0,
    allows_type2: " ",
    allows_type2amt: 0,
    allows_type3: " ",
    allows_type3amt: 0,
    allows_type4: " ",
    allows_type4amt: 0,
    allows_type5: " ",
    allows_type5amt: 0,
    allows_type6: " ",
    allows_type6amt: 0,
    allows_type7: " ",
    allows_type7amt: 0,
    allows_type8: " ",
    allows_type8amt: 0,
    deducts_type1: " ",
    deducts_type1amt: 0,
    deducts_type2: " ",
    deducts_type2amt: 0,
    deducts_type3: " ",
    deducts_type3amt: 0,
    deducts_type4: "",
    deducts_type4amt: 0,
    deducts_type5: "",
    deducts_type5amt: 0,
    deducts_type6: "",
    deducts_type6amt: 0,
    deducts_type7: "",
    deducts_type7amt: 0,
    deducts_type8: "",
    deducts_type8amt: 0,
  },
];

const PayForm = ({
  formdata,
  setFormdata,
  loadFormdata,
  setLoadFormdata,
  payitems,
  setLoadUpdatedata,
  rowindex,
}) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);
  const { singlebatchpayslip } = usePayslipsContext();

  useEffect(() => {
    setState(initial_state);
    setState({ ...formdata });
    setLoadFormdata(false);
    console.log("useeffect state", state);
  }, [loadFormdata]);

  const Update_Empdata = ({ name, value }) => {
    let data = singlebatchpayslip[rowindex];
    data[name] = value;
    console.log("update data", data);
    console.log("update payslip", singlebatchpayslip[rowindex]);
  };

  const handleChange = (e) => {
    e.preventDefault();
    setState({ ...state, [e.target.name]: e.target.value });
    Update_Empdata({ name: e.target.name, value: e.target.value });
  };

  const handleFormChange = (e) => {
    e.preventDefault();
    console.log("State", state);
    setFormdata({ ...state });
    setLoadUpdatedata(true);
  };

  const handleCalc = (e) => {
    e.preventDefault();
    const wages = state.basic_salary
    const totalTAP = Math.ceil(state.wages * 0.05);
    const totalSCP =
      Math.round((state.wages + Number.EPSILON) * 0.035 * 100) / 100;

    const allows =
      parseInt(state.allows_type2amt, 10) +
      parseInt(state.allows_type3amt, 10) +
      parseInt(state.allows_type4amt, 10) +
      parseInt(state.allows_type5amt, 10) +
      parseInt(state.allows_type6amt, 10) +
      parseInt(state.allows_type7amt, 10) +
      parseInt(state.allows_type8amt, 10); 
    
    const deducts =
      parseInt(state.deducts_type1amt, 10) +
      parseInt(state.deducts_type2amt, 10) +
      parseInt(state.deducts_type3amt, 10) +
      parseInt(state.deducts_type4amt, 10) +
      parseInt(state.deducts_type5amt, 10) +
      parseInt(state.deducts_type6amt, 10) +
      parseInt(state.deducts_type7amt, 10) +
      parseInt(state.deducts_type8amt, 10);
    
    const nettPay = state.wages - totalTAP - totalSCP + allows - deducts
    setState({
      ...state,
      allowances: allows,
      deductions: deducts,
      tapamount: totalTAP,
      scpamount: totalSCP,
      nett_pay: nettPay,
    });
  };

  console.log("payform", formdata);
