    <form>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={12} align="left" style={{ border: "1px solid white" }}>
          <div style={{ padding: 5 }}>
            <ButtonGroup
              color="primary"
              aria-label="outlined primary button group"
            >
              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
                style={{ marginLeft: 5 }}
              >
                Save <Icon className={classes.rightIcon}>send</Icon>
              </Button>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
                style={{ marginLeft: 10 }}
                onClick={handleCalc}
              >
                Calc <Icon className={classes.rightIcon}>send</Icon>
              </Button>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
                style={{ marginLeft: 10 }}
              >
                Summary <Icon className={classes.rightIcon}>send</Icon>
              </Button>
            </ButtonGroup>
          </div>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Allowances</h2>
        </Grid>

        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Deductions</h2>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type1"
                  variant="filled"
                  type="text"
                  value={state.allows_type1}
                  onChange={handleChange}
                  style={{ width: "100%" }}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  InputProps={{
                    readOnly: true,
                  }}
                ></TextField>
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type2"
                  variant="filled"
                  type="text"
                  displayEmpty
                  defaultValue=""
                  value={state.allows_type2}
                  onChange={handleChange}
                  style={{ width: "100%" }}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  InputProps={{
                    readOnly: true,
                  }}
                ></TextField>
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type3"
                  variant="filled"
                  type="text"
                  value={state.allows_type3}
                  onChange={(e) => {
                    handleChange(parseInt(e.target.value, 10));
                  }}
                  style={{ width: "100%" }}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  InputProps={{
                    readOnly: true,
                  }}
                ></TextField>
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type4"
                  variant="filled"
                  type="text"
                  value={state.allows_type4}
                  onChange={(e) => {
                    handleChange(parseInt(e.target.value, 10));
                  }}
                  style={{ width: "100%", textAlign: "left" }}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  select
                >
                  <ListSubheader>Wages</ListSubheader>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type == "Wages";
                    })
                    .map((row) => {
                      return (
                        <MenuItem key={row.id} value={row.name}>
                          {row.name}
                        </MenuItem>
                      );
                    })}
                  <ListSubheader>Allowances</ListSubheader>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type == "Allowances";
                    })
                    .map((row) => {
                      return (
                        <MenuItem key={row.id} value={row.name}>
                          {row.name}
                        </MenuItem>
                      );
                    })}
                </TextField>
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type5"
                  variant="filled"
                  type="text"
                  value={state.allows_type5}
                  onChange={handleChange}
                  style={{ width: "100%", textAlign: "left" }}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  select
                >
                  <ListSubheader>Wages</ListSubheader>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type == "Wages";
                    })
                    .map((row) => {
                      return (
                        <MenuItem key={row.id} value={row.name}>
                          {row.name}
                        </MenuItem>
                      );
                    })}
                  <ListSubheader>Allowances</ListSubheader>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type == "Allowances";
                    })
                    .map((row) => {
                      return (
                        <MenuItem key={row.id} value={row.name}>
                          {row.name}
                        </MenuItem>
                      );
                    })}
                </TextField>
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type6"
                  variant="filled"
                  type="text"
                  value={state.allows_type6}
                  onChange={handleChange}
                  style={{ width: "100%", textAlign: "left" }}
