                      return (
                        <MenuItem key={row.id} value={row.name}>
                          {row.name}
                        </MenuItem>
                      );
                    })}
                </TextField>
              </div>
            </Grid>
            <Grid
              item
              sm={4}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Amount"
                  name="allows_type1amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type1amt}
                  onChange={(e) => {
                    handleChange(parseInt(e.target.value, 10));
                  }}
                  style={{ width: "100%" }}
                  InputLabelProps={{
                    shrink: true,
