                ></TextField>
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="allows_type2amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type2amt}
                  onChange={handleChange}
                  InputLabelProps={{
