                  }}
                ></TextField>
              </div>

              <div>
                <TextField
                  label="Amount"
                  name="allows_type3amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type3amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                ></TextField>
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="allows_type4amt"
                  variant="filled"
