                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type3amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type3amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type4amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type4amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type5amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type5amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type6amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type6amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type7amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type7amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type8amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type8amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
            </Grid>
          </Grid>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div>
            <h3>{state.name}</h3>
          </div>
          <Divider variant="fullWidth" className={classes.divider} />
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="number"
              value={state.wages}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="TAP Amount"
              name="tapamount"
              variant="filled"
              type="number"
              value={state.tapamount}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="SCP Amounut"
              name="scpamount"
              variant="filled"
              type="number"
              value={state.scpamount}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Allowances"
              name="allowances"
              variant="filled"
              type="number"
              value={state.allowances}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Deductions"
              name="deductions"
              variant="filled"
              type="number"
              value={state.deductions}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nett_pay"
              variant="filled"
              type="number"
              value={state.nett_pay}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

