                            })}
                        </TextField>
                      )}
                    />
                  </div>
                  <div>
                    <Controller
                      name="payitem5"
                      control={control}
                      defaultValue={payslipsdata.pay[rowindex][0].payitem5}
                      render={({ field: { onChange, value } }) => (
                        <TextField
                          variant="filled"
                          type="text"
                          value={value}
                          style={{ width: 180 }}
                          value={value}
                          onChange={onChange}
                          select
                        >
                          <ListSubheader>Wages</ListSubheader>
                          {payitems
                            .filter(function (item) {
                              return item.pay_type == "Wages";
                            })
                            .map((row) => {
                              return (
                                <MenuItem
                                  key={row.id}
                                  value={row.name}
                                  style={{ width: 180 }}
                                >
                                  {row.name}
                                </MenuItem>
                              );
                            })}
                          <ListSubheader>Allowances</ListSubheader>
                          {payitems
                            .filter(function (item) {
