import { selector, useRecoilValue } from "recoil";
import { allowsperiodstate} from "./atomdata"
import { departments_url } from "../../utils/constants";
import { dailyallowances_url, dailyallowsdetls_url } from "../../utils/constants";

export const fetchDepartmentsSelector = selector({
  key: "departmentsSelector",
  get: async ({ get }) => {
    try {
      const res = await fetch(departments_url);
      const data = await res.json();

      return data;
    } catch (error) {
      throw error;
    }
  },
});

export const fetchDailyAllowancesSelector = selector({
  key: "dailyallowancesSelector",
  get: async ({ get }) => {
      try {
          const user = get(loggedInUserState);
        const period = useRecoilValue(allowsperiodstate)
      const res = await fetch(`${dailyallowances_url}?fv=${period}`);
      const data = await res.json();

      return data;
    } catch (error) {
      throw error;
    }
  },
});
