import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import {
  Button,
  ButtonGroup,
  Grid,
  Icon,
  TextField,
  Divider,
  ListSubheader,
  MenuItem,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";
import { payrunState } from "./data/atomdata";

const columns = [
  {
    title: "Name",
    field: "name",
    editable: "never",
  },
  {
    title: "Wages",
    field: "wages",
    editable: "never",
    type: "currency",
  },
  {
    title: "TAP Amount",
    field: "tapamount",
    editable: "never",
    type: "currency",
  },
  {
    title: "SCP Amount",
    field: "scpamount",
    editable: "never",
    type: "currency",
  },
  {
    title: "Allowances",
    field: "allowancws",
    editable: "never",
    type: "currency",
  },
  {
    title: "Deductions",
    field: "deductions",
    editable: "never",
    type: "currency",
  },
  {
    title: "Nett Pay",
    field: "nett_pay",
    editable: "never",
    type: "currency",
  },
];

const initial_state = [
  {
    totalwages: 0,
    totaltap: 0,
    totalscp: 0,
    totalallows: 0,
    totaldeducts: 0,
    totalpayroll: 0,
  },
];

const PaySummary = ({ singlebatchpayslip }) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);
  const [payrundata, setPayrundata] = useRecoilState(payrunState);
  const [isCalc, setIsCalc] = useState(true);

  const handleCalcTotals = () => {
    const data = singlebatchpayslip;
    const totalwages = data.reduce((acc, item) => {
      return acc + item.wages;
    }, 0);
    const totalallows = data.reduce((acc, item) => {
      return (
        acc +
        item.allows_type1amt +
        item.allows_type2amt +
        item.allows_type3amt +
        item.allows_type4amt +
        item.allows_type5amt +
        item.allows_type6amt +
        item.allows_type7amt +
        item.allows_type8amt
      );
    }, 0);
      const totaldeducts = data.reduce((acc, item) => {
        return (
          acc +
          item.deducts_type1amt +
          item.deducts_type2amt +
          item.deducts_type3amt +
          item.deducts_type4amt +
          item.deducts_type5amt +
          item.deducts_type6amt +
          item.deducts_type7amt +
          item.deducts_type8amt
        );
      }, 0);
    const totaltap = Math.ceil(totalwages * 0.05);
    const totalscp =
      Math.round((totalwages + Number.EPSILON) * 0.035 * 100) / 100;

    // setPayrundata({
    //   ...payrundata,
    //   totalpayroll: { totalpayroll },
    //   totalwages: { totalwages },
    //   totaltap: { totaltap },
    //   totalscp: { totalscp },
    //   totalallows: { totalallows },
    //   totaldeducts: { totaldeducts },
    // });
    console.log("payrundata", payrundata);
    console.log("totals", totalwages, totaltap, totalscp, totalallows);
  };

  useEffect(() => {
    handleCalcTotals();
    setIsCalc(false);
  }, [isCalc]);

  return (
    <form>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={12} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={3} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Period"
              name="period"
              variant="filled"
              type="text"
              value={payrundata.period}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
        <Grid item sm={3} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Pay Run Batch"
              name="payrun"
              variant="filled"
              type="text"
              value={payrundata.payrun}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
        <Grid item sm={3} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Pay Date"
              name="paydate"
              variant="filled"
              type="date"
              value={payrundata.paydate}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
        <Grid item sm={3} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Payroll Total"
              name="paytotal"
              variant="filled"
