import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import MaterialTable, { MTableToolbar } from "material-table";
import { makeStyles } from "@material-ui/core/styles";
import { TextField, Icon, Button, MenuItem } from "@material-ui/core";
import { useSetRecoilState, useRecoilValue, useRecoilState } from "recoil";
import {
  allowsPeriodState,
  allowsDataState,
  empidState,
  loginLevelState,
} from "./data/atomdata";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import CheckIcon from "@material-ui/icons/Check";
import SearchIcon from "@material-ui/icons/Search";
import { CustomDialog } from "../helpers/CustomDialog";
import { useDailyAllowancesContext } from "../context/dailyallowances_context";
import DailyAllowancesAddPeriod from "./DailyAllowancesAddPeriod";

const columns = [
  {
    title: "Name",
    field: "name",
    editable: "never",
  },
  { title: "Period", field: "period", editable: "never" },
  { title: "Location", field: "location", editable: "never" },
  { title: "Manager Name", field: "manager_name", editable: "never" },
  {
    title: "No Of Days",
    field: "no_of_days",
    type: "numeric",
    editable: "never",
  },
  { title: "Amount", field: "amount", type: "currency", editable: "never" },
  {
    title: "Status",
    field: "status",
    editComponent: (props) => (
      <TextField
        //defaultValue={props.value || null}
        onChange={(e) => props.onChange(e.target.value)}
        style={{ width: 100 }}
        value={props.value}
        select
      >
        <MenuItem value="Pending">Pending</MenuItem>
        <MenuItem value="Approve">Approve</MenuItem>
        <MenuItem value="Reject">Reject</MenuItem>
        <MenuItem value="Cancel">Cancel</MenuItem>
      </TextField>
    ),
  },
];

export default function DailyAllowancesTableStaff() {
  let history = useHistory();
  const classes = useStyles();
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const [isAddPeriodDialogOpen, setIsAddPeriodDialogOpen] = useState(false);
  const [tmpallowsdata, setTmpallowsdata] = useState([]);
  const allows_period = useRecoilValue(allowsPeriodState);
  const allows_empid = useRecoilValue(empidState);
  const [allowsdata, setAllowsdata] = useRecoilState(allowsDataState);
  const setEmpID = useSetRecoilState(empidState);
  const title = `Site Allowances`;
  const {
    dailyallowances,
    loadEmpDailyAllowances,
    dailyallowances_loading,
    dailyallowances_error,
    updateDailyAllowance,
  } = useDailyAllowancesContext();

  useEffect(() => {
    loadEmpDailyAllowances(loginLevel.loginUserId);
  }, []);

  const Save_DailyAllowancesData = () => {
    dailyallowances.forEach((data) => {
      const { id } = data;
      if (id) {
        const { id, rec_id, tableData, ...fields } = data;
        updateDailyAllowance({ id, ...fields });
      }
    });

    //handleDialogClose();
  };

  const handleAddPeriodDialogOpen = () => {
    //console.log("allowsdetls", allowsdata, allowsdataRef, singlebatch_dailyallowsdetl);
    //setAllowsdata({ singlebatch_dailyallowsdetl });
    setIsAddPeriodDialogOpen(true);
  };

  const handleAddPeriodDialogClose = () => {
    setIsDialogOpen(false);
    //getSingleBatchDailyAllowance(dailyallowance_period);
  };

  if (dailyallowances_loading) {
    return (
      <div>
        <h2>Loading... site allowances</h2>
      </div>
    );
  }
  if (dailyallowances_error) {
    return (
      <div>
        <h2>Internet connection problem!</h2>
      </div>
    );
  }

  return (
    <div className={classes.root}>
      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={dailyallowances}
          title={title}
          icons={{
            Add: (props) => <AddIcon />,
            Edit: (props) => <EditIcon />,
            Delete: (props) => <DeleteIcon />,
            Clear: (props) => <DeleteIcon />,
            Check: (props) => <CheckIcon />,
            Search: (props) => <SearchIcon />,
            ResetSearch: (props) => <DeleteIcon />,
          }}
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                // setTimeout(() => {
                //   setAllowsdata([...allowsdata, newData]);
                //   resolve();
                // }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  const dataUpdate = [...dailyallowances];
                  const index = oldData.tableData.id;
                  dataUpdate[index] = newData;
                  //setDailyAllowances([...dataUpdate]);
                  //approve_Expense(newData);

                  resolve();
                }, 1000);
              }),
          }}
          options={{
            filtering: true,
            headerStyle: {
              backgroundColor: "orange",
              color: "#FFF",
            },
            showTitle: true,
          }}
          // components={{
          //   Toolbar: (props) => (
          //     <div>
          //       <MTableToolbar {...props} />
          //       <div style={{ padding: "5px 10px" }}>
          //         <Button
          //           type="submit"
          //           variant="contained"
          //           color="secondary"
          //           className={classes.button}
          //           onClick={Save_DailyAllowancesData}
          //         >
          //           Update <Icon className={classes.rightIcon}>send</Icon>
          //         </Button>
          //       </div>
          //     </div>
          //   ),
          // }}
        />
        <div className={classes.dialog}>
          <CustomDialog
            isOpen={isAddPeriodDialogOpen}
            //handleClose={handleDialogClose}
            title=""
            showButton={true}
            isFullscreen={true}
            isFullwidth={true}
          >
            <DailyAllowancesAddPeriod
              setAllowsdata={setAllowsdata}
              allowsdata={allowsdata}
              handleDialogClose={handleAddPeriodDialogClose}
            />
          </CustomDialog>
        </div>
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
  dialog: {
    width: 1000,
  },
}));
