import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import {
  Button,
  ButtonGroup,
  Grid,
  Icon,
  TextField,
  Divider,
  ListSubheader,
  MenuItem,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { singlebatchpayslip } from "../context/payslips_context";
import { usePayslipsContext } from "../context/payslips_context";

const columns = [
  {
    title: "Name",
    field: "name",
    editable: "never",
    cellStyle: {
      width: 200,
      maxWidth: 200,
    },
  },
  {
    title: "Wages",
    field: "wages",
    editable: "never",
    type: "currency",
    cellStyle: {
      width: 100,
      maxWidth: 100,
    },
  },
  {
    title: "Wages",
    field: "wages",
    editable: "never",
    type: "currency",
    cellStyle: {
      width: 100,
      maxWidth: 100,
    },
  },
];

const initial_state = [
  {
    name: "",
    period: "",
    date: "",
    payrun: "",
    nett_pay: 0,
    bank_name: "",
    bank_acno: "",
    tap_acno: "",
    tap_amount: 0,
    scp_acno: "",
    scp_amount: 0,
    empid: "",
    status: "",
    allows_type1: " ",
    allows_type1amt: 0,
    allows_type2: " ",
    allows_type2amt: 0,
    allows_type3: " ",
    allows_type3amt: 0,
    allows_type4: " ",
    allows_type4amt: 0,
    allows_type5: " ",
    allows_type5amt: 0,
    allows_type6: " ",
    allows_type6amt: 0,
    allows_type7: " ",
    allows_type7amt: 0,
    allows_type8: " ",
    allows_type8amt: 0,
    deducts_type1: " ",
    deducts_type1amt: 0,
    deducts_type2: " ",
    deducts_type2amt: 0,
    deducts_type3: " ",
    deducts_type3amt: 0,
    deducts_type4: "",
    deducts_type4amt: 0,
    deducts_type5: "",
    deducts_type5amt: 0,
    deducts_type6: "",
    deducts_type6amt: 0,
    deducts_type7: "",
    deducts_type7amt: 0,
    deducts_type8: "",
    deducts_type8amt: 0,
  },
];

const PaySummary = ({
  formdata,
  setFormdata,
  loadFormdata,
  setLoadFormdata,
  payitems,
  setLoadUpdatedata,
  rowindex,
}) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);
  const { singlebatchpayslip } = usePayslipsContext();

  return (
    <form>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={12} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Pay Period</h2>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Payment Date</h2>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Payroll Total</h2>
        </Grid>
      </Grid>

      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <MaterialTable
          columns={columns}
          data={singlebatchpayslip}
          title="Payroll"
          options={{
            filtering: false,
            search: false,
            toolbar: false,

            headerStyle: {
              backgroundColor: "orange",
              color: "primary",
            },
            showTitle: false,
          }}
        />
      </Grid>
    </form>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  button: {
    margin: theme.spacing(1),
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "80vw",
    margin: "1rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },
  divider: {
    // Theme Color, or use css color in quote
    background: "white",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 350,
  },
  divContainer: {
    display: "flex",
    flexDirection: "row",
  },
}));

export default PaySummary;
