import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import dateFormat from 'dateformat'
import {format} from 'date-fns'
import axios from "axios";
import { useAsync } from "react-async";
import {
  Button,
  Paper,
  Grid,
  Icon,
  Divider,
  TextField,
  MenuItem,
} from "@material-ui/core";
import { employees_url } from "../utils/constants";
import { usePayslipsContext } from "../context/payslips_context";

const drawerWidth = 240;
const url = "https://course-api.com/react-tabs-project";

// const loadEmp = async () =>
//   await fetch(employees_url)
//     .then((res) => (res.ok ? res : Promise.reject(res)))
//     .then((res) => res.json());

const Payrun = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const { addPayrun } = usePayslipsContext();
  const [input, setInput] = useState({
    payfreq: "",
    fromdate: null,
    todate: null,
      paydate: null,
    period: "",
  });

  const handleChange = (e) => {
    setInput({ ...input, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
      e.preventDefault();
      const p = input.fromdata.getDay 
      setInput({ ...input, period: p })
    console.log(input)
    //update payrun
    // addPayrun({
    //   pay_freq: input.payfreq,
    //   from_date: input.fromdate,
    //   to_date: input.todate,
    //   pay_date: input.paydate,
    // });
  };

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
        <Grid container item sm={8}>
          <article className={classes.jobinfo}>
            <h2>Pay Run</h2>
            <form onSubmit={handleSubmit}>
              <div>
                <TextField
                  label="Pay Frequency"
                  variant="filled"
                  required
                  style={{ width: 350 }}
                  name="payfreq"
                  value={input.payfreq}
                  onChange={(e) => handleChange(e)}
                  select
                >
                  <MenuItem value="Weekly">Weekly</MenuItem>
                  <MenuItem value="Monthly">Monthly</MenuItem>
                </TextField>
              </div>
              <div>
                <TextField
                  label="From Date"
                  variant="filled"
                  name="fromdate"
                  type="date"
                  value={input.fromdate}
                  required
                  style={{ width: 350 }}
                  onChange={(e) => handleChange(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="To Date"
                  variant="filled"
                  type="date"
                  name="todate"
                  value={input.todate}
                  required
                  onChange={(e) => handleChange(e)}
                  style={{ width: 350 }}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Payment Date"
                  variant="filled"
                  type="date"
                  name="paydate"
                  value={input.paydate}
                  required
                  style={{ width: 350 }}
                  onChange={(e) => handleChange(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  className={classes.button}
                >
                  Submit <Icon className={classes.rightIcon}>send</Icon>
                </Button>
              </div>
            </form>
          </article>
        </Grid>
      </section>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  button: {
    margin: theme.spacing(1),
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "70vw",
    margin: "5rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },
  rightIcon: {
    marginLeft: theme.spacing(1),
  },
}));

export default Payrun;
