            <h3>{state.name}</h3>
          </div>
          <Divider variant="fullWidth" className={classes.divider} />
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="number"
              value={state.wages}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="TAP Amount"
              name="tapamount"
              variant="filled"
              type="number"
              value={state.tapamount}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="SCP Amounut"
              name="scpamount"
              variant="filled"
              type="number"
              value={state.scpamount}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Allowances"
              name="allowances"
              variant="filled"
              type="number"
              value={state.allowances}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Deductions"
              name="deductions"
              variant="filled"
              type="number"
              value={state.deductions}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nettpay"
              variant="filled"
              type="number"
              value={state.nettpay}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

