import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { List, ListItem, Grid } from "@material-ui/core";
import clsx from "clsx";
import axios from "axios";
import { useAsync} from "react-async"
import MaterialTable from "material-table";
import Paper from "@material-ui/core/Paper";
import {
  atom,
  selector,
  useRecoilValue,
  useRecoilState,
  useSetRecoilState,
  useRecoilValueLoadable,
  useRecoilStateLoadable,
} from "recoil";
// import { useRecoilValue, useSetRecoilState, useRecoilState } from "recoil";
// import { departmentsSelector } from "../helpers/Recoilhelpers";
import { onleaves_url } from "../utils/constants";
import { fetchDepartmentsSelector } from "./data/selectordata";

const drawerWidth = 240;

const columns = [
  {
    title: "Name",
    field: "name",
    editable: "never",
  },
  {
    title: "From Data",
    field: "from_date",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
  },
  {
    title: "To Data",
    field: "to_date",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
  },
];

export const onleavesdatastate = atom({
  key: "onleavesdatastate",
  default: [],
});

const fetchOnLeavesDetails = selector({
  key: "onLeaveDetailsSelector",
  get: async ({ get }) => {
    try {
      const { data } = await axios.get(onleaves_url);
      const onleavesdata = data;

      return data;
    } catch (error) {
      throw error;
    }
  },
});

const fetchOnDetails = async({ playerId }, { signal }) =>({
  key: "onLeaveDetailsSelector",
  get: async ({ get }) => {
    try {
      const { data } = await axios.get(onleaves_url);
      const onleavesdata = data;

      return data;
    } catch (error) {
      throw error;
    }
  },
});

const OnLeavesView = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [userdata, setUserdata] = useState([]);
  //const [userdata, setUserdata] = useRecoilState(userdatastate);
  //const onLeavesDetails = useRecoilValueLoadable(fetchOnLeavesDetails);
  const { data, error, isPending } = useAsync({
    promiseFn: fetchOnLeavesDetails,
    playerId: 1,
  });

  if (error) {
    return <div> There is Internet connection problem! </div>;
  }

  if (isPending) {
    return (
      <div>
        <h2>Loading....On Leaves</h2>
      </div>
    );
  }

  if (data) {
    // const editable = contents.map((r) => {
    //   return { ...r };
    // });
    return (
      <div className={classes.root}>
        <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
          {/* <MaterialTable
            columns={columns}
            data={editable}
            title="Staffs On Leave within 30 Days"
            options={{
              filtering: false,
              search: false,
              toolbar: false,
              exportButton: true,
              headerStyle: {
                backgroundColor: "orange",
                color: "primary",
              },
              showTitle: false,
            }}
          /> */}

          <Grid container>
            {data.map((r) => {
              return (
                <li key={r.id}>
                  <div style={{ wordSpacing: 10 }}>
                          <Grid item xs={6 } sm={6} lg={3}>
                      {r.name}
                    </Grid>
                          <Grid item xs={6} sm={6} lg={3}>
                      {r.from_date}
                    </Grid>
                          <Grid item xs={6} sm={6} lg={3}>
                      {r.to_date}
                    </Grid>
                  </div>
                </li>
              );
            })}
          </Grid>
        </div>
      </div>
    );
  }
};

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(1), //grid padding
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));

export default OnLeavesView;
