import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Paper from "@material-ui/core/Paper";
import { selector, useRecoilValue, useRecoilValueLoadable } from "recoil";
// import { useRecoilValue, useSetRecoilState, useRecoilState } from "recoil";
import { departmentsSelector } from "../helpers/Recoilhelpers";
import { departments_url } from "../utils/constants";

const url = `https://reqres.in/api/users?page=1`;
const drawerWidth = 240;

const fetchUserDetails = selector({
  key: "userDetailsSelector",
  get: async ({ get }) => {
    try {
      const res = await fetch(url);
      const data = await res.json();
      console.log(data);
      return data;
    } catch (error) {
      throw error;
    }
  },
});

const DetailsWithoutSuspense = () => {
   const classes = useStyles();
   const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const userDetails = useRecoilValueLoadable(fetchUserDetails);
  const { state } = userDetails;

  if (userDetails.state === "hasError") {
    return <div> There is some problem! </div>;
  }

  if (state === "loading") {
    return <div>Its loading</div>;
  }

  if (state === "hasValue") {
    const {
      contents: { data },
    } = userDetails;
    return data.map((item) => (
      <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
        <div key={item.id}>
          <p>
            {`Email: ${item.email} Name: ${item.first_name} ${item.last_name}`}
          </p>
        </div>
      </Paper>
    ));
  }
};

const DetailsWithSuspense = () => {
  const userDetails = useRecoilValue(fetchUserDetails);
  const { data } = userDetails;

  return data.map((item) => (
    <div key={item.id}>
      <p>{`Email: ${item.email} Name: ${item.first_name} ${item.last_name}`}</p>
    </div>
  ));
};

const Example = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const departsdata = useRecoilValue(departmentsSelector);

  return (
    <div>
      <DetailsWithoutSuspense />
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
}));

export default Example;
