];

const PayForm = ({
  formdata,
  setFormdata,
  loadFormdata,
  setLoadFormdata,
  payitems,
  setLoadUpdatedata,
}) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);

  useEffect(() => {
    setState({ ...formdata });
    setLoadFormdata(false);
    console.log("payform state", state);
  }, [loadFormdata]);



  
  const handleChange = (e) => {
    e.preventDefault();
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const handleFormChange = (e) => {
    e.preventDefault();
    console.log("State", state);
    setFormdata({ ...state });
    setLoadUpdatedata(true);
  };

  console.log("payform", formdata);
