      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type1"
                  variant="filled"
                  type="text"
                  value={state.allows_type1}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                ></TextField>
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type2"
                  variant="filled"
                  type="text"
                  value={state.allows_type2}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  select
                >
                  <ListSubheader>Wages</ListSubheader>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type == "Wages";
                    })
                    .map((row) => {
                      return (
                        <MenuItem
                          key={row.id}
                          value={row.name}
                          style={{ width: 200, textAlign: "left" }}
                        >
                          {row.name}
                        </MenuItem>
                      );
                    })}
                  <ListSubheader>Allowances</ListSubheader>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type == "Allowances";
                    })
                    .map((row) => {
                      return (
                        <MenuItem key={row.id} value={row.name}>
                          {row.name}
                        </MenuItem>
                      );
                    })}
                </TextField>
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type3"
                  variant="filled"
                  type="text"
                  value={state.allows_type3}
