            <TextField
              label="Total Deductions"
              name="deductions"
              variant="filled"
              type="number"
              value={state.deductions}
              onChange={handleChange}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nettpay"
              variant="filled"
              type="number"
              value={state.nettpay}
              onChange={handleChange}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

