import React, { useState, useReducer, useEffect } from "react";
import {
  Button,
  Icon,
  TextField,
  Paper,
  Typography,
  Select,
  Divider,
  Grid,
  Toolbar,
  Box,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import { useEmployeesContext } from "../context/employees_context";
import { usePayslipsContext } from "../context/payslips_context";
import { Controller, useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";

import Payslip_Earnings from "./Payslip_Earnings";
import Payslip_Deductions from "./Payslip_Deductions";

const initial_values = {
  name: "",
  period: "",
  date: "",
  bank_name: "",
  bank_acno: "",
  status: "Pending",
  total_earnings: 0,
  total_deductions: 0,
  tap_amount: 0,
  scp_amount: 0,
  basic_pay: 0,
  nett_pay: 0,
};

const PayslipForm = () => {
  let history = useHistory();
  const classes = useStyles();
  const {
    isPayslipEditing,
    single_payslip,
    updatePayslip,
    addPayslip,
    editPayslipID,
    loadPayslips,
    single_payslip_loading,
    payslip_period,
    payslip_earning_amount,
    payslip_deduction_amount,
  } = usePayslipsContext();
  const { loadEmployees, employees } = useEmployeesContext();
  const [state, setState] = useState(initial_values);

  // const {
  //   name,
  //   period,
  //   date,
  //   bank_name,
  //   bank_acno,
  //   status,
  //   total_earnings,
  //   total_deductions,
  //   tap_amount,
  //   scp_amount,
  //   basic_pay,
  //   nett_pay,
  // } = state;

  // useEffect(() => {
  //   loadPayslips();
  //   update_state();
  // }, []);

  // const update_state = () => {
  //   console.log("single", single_payslip);
  //   setState({ ...state, bank_name: single_payslip.bank_name });
  // };

  const handleChange = (event) => {
    const value =
      event.target.type === "number"
        ? parseInt(event.target.value, 10)
        : event.target.value;
    setState({ ...state, [event.target.name]: value });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
  };

  const handleCalc = () => {
    setState({
      ...state,
      total_earnings: payslip_earning_amount,
      total_deductions: payslip_deduction_amount,
    });
  };
  const calc_totals = () => {};

  const onSubmit = (data) => {
    console.log(data);
    // if (isPayslipEditing) {
    //   updatePayslip({ id: editPayslipID, ...data });
    // } else {
    //   addPayslip({ ...data });
    // }
    // loadPayslips();
    // history.push("/batchpayslips");
  };

  if (single_payslip_loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <Paper className={classes.root}>
          <Grid container className={classes.root} spacing={15}>
            <div>
              <Typography variant="h5" component="h3">
                PAYSLIP FORM
              </Typography>
              <Typography component="p">Payslip Preparation</Typography>

              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
              >
                Submit <Icon className={classes.rightIcon}>send</Icon>
              </Button>
            </div>
          </Grid>

          <Grid item xs={12}>
            <div>
              <TextField
                label="Name"
                id="margin-normal"
                name="name"
                value={name}
                className={classes.textField}
                onChange={handleChange}
                // select
              >
                {/* {employees.map((e) => {
                  return (
                    <MenuItem key={e.name} value={e.name}>
                      {e.name}
                    </MenuItem>
                  ); */}
                })}
              </TextField>
            </div>
            <div>
              <TextField
                label="Period"
                id="margin-normal"
                name="period"
                value={period}
                className={classes.textField}
                onChange={handleChange}
                InputProps={{
                  readOnly: true,
                }}
              />

              <TextField
                label="Date"
                type="date"
                id="margin-normal"
                name="date"
                value={date}
                className={classes.textField}
                onChange={handleChange}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </div>
            <div>
              <TextField
                label="Bank Name"
                id="margin-normal"
                name="bank_name"
                value={bank_name}
                className={classes.textField}
                onChange={handleChange}
              />

              <TextField
                label="Bank AC No"
                id="margin-normal"
                name="bank_acno"
                value={bank_acno}
                className={classes.textField}
                onChange={handleChange}
              />
            </div>
            <div>
              <TextField
                label="TAP Contrinution"
                type="number"
                id="standard-number"
                name="tap_amount"
                value={tap_amount}
                className={classes.textField}
                //onChange={onChange}
                onChange={(e) => {
                  handleChange(parseInt(e.target.value, 10));
                }}
              />

              <TextField
                label="SCP Contribution"
                type="number"
                id="standard-number"
                name="scp_amount"
                value={scp_amount}
                className={classes.textField}
                //onChange={onChange}
                onChange={(e) => {
                  handleChange(parseInt(e.target.value, 10));
                }}
              />
            </div>
            <div>
              <TextField
                label="Total Earnings"
                type="number"
                id="standard-number"
                name="total_earnings"
                value={total_earnings}
                className={classes.textField}
                //onChange={onChange}
                onChange={(e) => {
                  handleChange(parseInt(e.target.value, 10));
                }}
              />

              <TextField
                label="Total Deductions"
                type="number"
                id="standard-number"
                name="total_deductions"
                value={total_deductions}
                className={classes.textField}
                //onChange={onChange}
                onChange={(e) => {
                  handleChange(parseInt(e.target.value, 10));
                }}
              />
            </div>
            <div>
              <TextField
                label="Basic Pay"
                type="number"
                id="standard-number"
                name="basic_pay"
                value={basic_pay}
                className={classes.textField}
                onChange={handleChange}
                // onChange={(e) => {
                //   handleChange(parseInt(e.target.value, 10));
                // }}
              />

              <TextField
                label="Nett Pay"
                type="number"
                id="standard-number"
                name="nett_pay"
                value={nett_pay}
                className={classes.textField}
                //onChange={onChange}
                onChange={(e) => {
                  handleChange(parseInt(e.target.value, 10));
                }}
              />
            </div>

            <div>
              <TextField
                label="Status"
                id="margin-normal"
                name="status"
                value={status}
                className={classes.textField}
                onChange={handleChange}
                select
              >
                <MenuItem value="Pending">Pending</MenuItem>
                <MenuItem value="Cancel">Cancel</MenuItem>
                <MenuItem value="Approved">Approved</MenuItem>
                <MenuItem value="Rejected">Rejected</MenuItem>
              </TextField>
            </div>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              className={classes.button}
              onClick={handleCalc}
            >
              Calc <Icon className={classes.rightIcon}>send</Icon>
            </Button>
          </Grid>
          <Divider />
          <div>
            <Grid container alignItems="center" className={classes.grid}>
              {/* <Grid item xs={4} style={{ textAlign: "center" }}> */}
              <Grid xs={12}>
                <Payslip_Earnings />
              </Grid>
              <Divider orientation="vertical" flexItem />
              {/* <Grid item xs={4} style={{ textAlign: "center" }}> */}
