import React, { useState } from "react";
import { Button, ButtonGroup, TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";
import { loginLevelState } from "./data/atomdata";
import { useEmployeesContext } from "../context/employees_context";

const SigninForm = ({ setLogin }) => {
  const classes = useStyles();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [alert, setAlert] = useState(true);
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const { single_employees, getSingleEmployeeEmail } = useEmployeesContext();

  const handleSubmit = (e) => {
    e.preventDefault();
    getSingleEmployeeEmail(email);

    if (single_employee.email === email && single_employee.password === password) {
      setPassword("");
    } else {
      setAlert(false);
    }
  };

  const handleStaffClick = (e) => {
    e.preventDefault();
    setLoginLevel({ ...loginLevel, loginLevel: "Staff", login: true });
    handleSubmit(e);
  };
  const handleAdminClick = (e) => {
    e.preventDefault();
    setLoginLevel({ ...loginLevel, loginLevel: "Admin", login: true });
    handleSubmit(e);
  };
  const handleAdminManagerClick = (e) => {
    e.preventDefault();
    setLoginLevel({ ...loginLevel, loginLevel: "AdminManager", login: true });
    handleSubmit(e);
  };
  const handleManagerClick = (e) => {
    e.preventDefault();
    setLoginLevel({ ...loginLevel, loginLevel: "Manager", login: true });
    handleSubmit(e);
  };

  return (
    <form className={classes.root} onSubmit={handleSubmit}>
      <TextField
        label="Email"
        variant="filled"
        type="email"
        required
        style={{ width: 350 }}
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <TextField
        label="Password"
        variant="filled"
        type="password"
        style={{ width: 350 }}
        required
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <div style={{ textAlign: "center" }}>
        <ButtonGroup>
          <Button onClick={(e) => handleStaffClick(e)}>Staff</Button>
          <Button onClick={handleAdminClick}>Admin</Button>
          <Button onClick={handleAdminManagerClick}>Admin Manager</Button>
          <Button onClick={handleManagerClick}>Manager</Button>
        </ButtonGroup>
      </div>
      {!alert && <h3>Login Fail!</h3>}
    </form>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: theme.spacing(2),

    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "600px",
    },
    "& .MuiButtonBase-root": {
      margin: theme.spacing(2),
    },
  },
}));

export default SigninForm;
