import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Paper from "@material-ui/core/Paper";
import {
  atom,
  selector,
  useRecoilValue,
  useSetRecoilState,
  useRecoilValueLoadable,
} from "recoil";
// import { useRecoilValue, useSetRecoilState, useRecoilState } from "recoil";
import { departmentsSelector } from "../helpers/Recoilhelpers";
import { departments_url } from "../utils/constants";

const url = `https://reqres.in/api/users?page=1`;

const drawerWidth = 240;

const useratastate = atom({
  id: 'userdata',
  default: []
})

const fetchUserDetails = selector({
  key: "userDetailsSelector",
  get: async ({ get }) => {
    try {
      const res = await fetch(departments_url);
      const data = await res.json();

      return data;
    } catch (error) {
      throw error;
    }
  },
});

const Example = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const userDetails = useRecoilValueLoadable(fetchUserDetails);
  const { state, contents } = userDetails;
  const userdata = useRecoilValue(userdata)
  const setUserdata = useSetRecoilState(userdatastate)
  setUserdata(userDetails.contents)

  console.log(userDetails)
  if (userDetails.state === "hasError") {
    return <div> There is some problem! </div>;
  }

  if (state === "loading") {
    return <div>Its loading</div>;
  }

  if (state === "hasValue") {
    //const { name } = userDetails;
    return (
      <paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
        {userdata.map((item) => {
          return (
            <div>
              <p>{`Name: ${item.name}`}</p>
            </div>
          );
        })}
      </paper>
    );
  }
};

