import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import MaterialTable from "material-table";
import Paper from "@material-ui/core/Paper";
import {
  atom,
  selector,
  useRecoilValue,
  useRecoilState,
  useSetRecoilState,
  useRecoilValueLoadable,
  useRecoilStateLoadable,
} from "recoil";
// import { useRecoilValue, useSetRecoilState, useRecoilState } from "recoil";
// import { departmentsSelector } from "../helpers/Recoilhelpers";
import { onleaves_url } from "../utils/constants";
import { fetchDepartmentsSelector } from "./data/selectordata";



const drawerWidth = 240;

const columns = [
  {
    title: "Name",
    field: "name",
    editable: "never",
  },
  {
    title: "From Data",
    field: "from_date",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
  },
];

export const userdatastate = atom({
  key: "userdata",
  default: [],
  dangerouslyAllowMutability: true,
});

const fetchUserDetails = selector({
  key: "userDetailsSelector",
  get: async ({ get }) => {
    try {
      const res = await fetch(departments_url);
      const data = await res.json();

      return data;
    } catch (error) {
      throw error;
    }
  },
});

const Example = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [userdata, setUserdata] = useState([]);
  //const [userdata, setUserdata] = useRecoilState(userdatastate);
  const userDetails = useRecoilValueLoadable(fetchDepartmentsSelector);
  const { state, contents } = userDetails;

  console.log(userDetails);
  if (userDetails.state === "hasError") {
    return <div> There is some problem! </div>;
  }

  if (state === "loading") {
    return <div>Its loading</div>;
  }

  if (state === "hasValue") {
    //  const editable = contents.map((r) => {
    //    return { ...r };
    //  });
    contents.map((r) => {
      const rec = [r.name];
      setUserdata([...userdata, r.name]);
    });
    //setUserdata(editable)
    console.log(userdata);
    //setUserdata(editable);
    return (
      <paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
        <MaterialTable
          columns={columns}
          data={userdata}
          title="Family"
          options={{
            search: false,
            toolbar: false,
          }}
        />
      </paper>
    );
  }
};


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
}));

export default Example;
