                            })
                            .map((row) => {
                              return (
                                <MenuItem key={row.id} value={row.name}>
                                  {row.name}
                                </MenuItem>
                              );
                            })}
                        </TextField>
                      )}
                    />
                  </div>
                  <div>
                    <Controller
                      name="payitem3"
                      control={control}
                      defaultValue={payslipsdata.pay[value][0].payitem3}
                      render={({ field: { onChange, value } }) => (
                        <TextField
