import React, { useState, useEffect, useRef } from "react";
import ReactToPrint from "react-to-print";
import MaterialTable from "material-table";
import { Button, Icon, Grid, TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useRecoilState } from "recoil";
import { payrunState } from "./data/atomdata";
import { usePayslipsContext } from "../context/payslips_context";

const columns = [
  {
    title: "Name",
    field: "name",
    editable: "never",
  },
  {
    title: "Wages",
    field: "wages",
    editable: "never",
    type: "currency",
  },
  {
    title: "TAP Amount",
    field: "tap_amount",
    editable: "never",
    type: "currency",
  },
  {
    title: "SCP Amount",
    field: "scp_amount",
    editable: "never",
    type: "currency",
  },
  {
    title: "Allowances",
    field: "total_allowancws",
    editable: "never",
    type: "currency",
  },
  {
    title: "Deductions",
    field: "total_deductions",
    editable: "never",
    type: "currency",
  },
  {
    title: "Nett Pay",
    field: "nett_pay",
    editable: "never",
    type: "currency",
  },
];

const initial_state = [
  {
    totalwages: 0,
    totaltap: 0,
    totalscp: 0,
    totalallows: 0,
    totaldeducts: 0,
    totalpayroll: 0,
  },
];

const PaySummary = ({ singlebatchpayslip }) => {
  const classes = useStyles();
  const componentRef = useRef();
  const [state, setState] = useState(initial_state);
  const [payrundata, setPayrundata] = useRecoilState(payrunState);
  const [isCalc, setIsCalc] = useState(true);
  const { payrun, updatePayrun, payslip_period } = usePayslipsContext();

  const ComponentToPrint = () => {
    return (
      <div>
        <h2>Hello</h2>
        <table>
          <thead>
            <div>Title 1</div>

            <th>column 1</th>
            <th>column 2</th>
            <th>column 3</th>
          </thead>
          <tbody>
            <tr>
              <td>data 1</td>
              <td>data 2</td>
              <td>data 3</td>
            </tr>
            <tr>
              <td>data 1</td>
              <td>data 2</td>
              <td>data 3</td>
            </tr>
            <tr>
              <td>data 1</td>
              <td>data 2</td>
              <td>data 3</td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  };

  // const handlePrint = useReactToPrint({
  //   content: () => componentRef.current,
  // });

  const handle_Print = (e) => {
    e.preventDefault();
    <Button>Print</Button>
  };

  const handleCalcTotals = () => {
    const data = singlebatchpayslip;
    const totalwages = data.reduce((acc, item) => {
      return acc + item.wages;
    }, 0);
    const totalallows = data.reduce((acc, item) => {
      return (
        acc +
        item.allows_type1amt +
        item.allows_type2amt +
        item.allows_type3amt +
        item.allows_type4amt +
        item.allows_type5amt +
        item.allows_type6amt +
        item.allows_type7amt +
        item.allows_type8amt
      );
    }, 0);
    const totaldeducts = data.reduce((acc, item) => {
      return (
        acc +
        item.deducts_type1amt +
        item.deducts_type2amt +
        item.deducts_type3amt +
        item.deducts_type4amt +
        item.deducts_type5amt +
        item.deducts_type6amt +
        item.deducts_type7amt +
        item.deducts_type8amt
      );
    }, 0);
    const totaltap = Math.ceil(totalwages * 0.05);
    const totalscp =
      Math.round((totalwages + Number.EPSILON) * 0.035 * 100) / 100;
    const totalpayroll =
      totalwages + totalallows - totaltap - totalscp - totaldeducts;
    setPayrundata({
      ...payrundata,
      totalpayroll: totalpayroll,
      totalwages: totalwages,
      totaltap: totaltap,
      totalscp: totalscp,
      totalallows: totalallows,
      totaldeducts: totaldeducts,
    });
    payrun
      .filter((r) => r.payrun === payslip_period)
      .map((rec) => {
        //update payrun
        return updatePayrun({
          id: rec.id,
          totalpayroll: totalpayroll,
          totalwages: totalwages,
          totaltap: totaltap,
          totalscp: totalscp,
          totalallows: totalallows,
          totaldeducts: totaldeducts,
        });
      });
    console.log("payrundata", payrundata);
    console.log(
      "totals",
      totalpayroll,
      totalwages,
      totaltap,
      totalscp,
      totalallows,
      totaldeducts
    );
  };

  const handleSaveCalcTotals = (e) => {
    e.preventDefault();
    handleCalcTotals();
  };

  useEffect(() => {
    handleCalcTotals();
    setIsCalc(false);
  }, [isCalc]);

  return (
    <div>
      {/* <div style={{ display: "none" }}> */}
      {/* <div>
        <div>
          <ReactToPrint
            trigger={() => <button>Print this out!</button>}
            content={() => this.componentRef}
          />
          <ComponentToPrint ref={(el) => (this.componentRef = el)} />
        </div>
      </div> */}
      <form>
        <Grid container direction="row" style={{ border: "1px solid white" }}>
          <Grid
            item
            sm={12}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              {/* <Button
              type="submit"
              variant="contained"
              color="primary"
              className={classes.button}
              style={{ marginLeft: 5 }}
              onClick={(e) => handleSaveCalcTotals(e)}
            >
              Save <Icon className={classes.rightIcon}>send</Icon>
            </Button> */}
              <h2>Summary</h2>
              {/* <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
                style={{ marginLeft: 5 }}
                onClick={(e) => handle_Print(e)}
              >
                Print <Icon className={classes.rightIcon}>send</Icon>
              </Button> */}
              {/* <div>
                <ReactToPrint
                  trigger={(e) => {
                    handle_Print(e);
                  }}
                  content={() => this.componentRef}
                />
                <ComponentToPrint ref={(el) => (this.componentRef = el)} />
              </div> */}
            </div>
          </Grid>
        </Grid>
        <Grid container direction="row" style={{ border: "1px solid white" }}>
          <Grid
            item
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Period"
                name="period"
                variant="filled"
                type="text"
                value={payrundata.period}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
          <Grid
            item
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Pay Run Batch"
                name="payrun"
                variant="filled"
                type="text"
                value={payrundata.payrun}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
          <Grid
            item
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Pay Date"
                name="paydate"
                variant="filled"
                type="date"
                value={payrundata.paydate}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
          <Grid
            item
            sm={3}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Payroll Total"
                name="paytotal"
                variant="filled"
                type="number"
                value={payrundata.totalpayroll}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
        </Grid>

        <Grid container direction="row" style={{ border: "1px solid white" }}>
          <Grid
            item
            sm={12}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <MaterialTable
              columns={columns}
              data={singlebatchpayslip}
              title="Payroll"
              options={{
                filtering: false,
                search: false,
                toolbar: false,

                headerStyle: {
                  backgroundColor: "orange",
                  color: "primary",
                },
                showTitle: false,
              }}
            />
          </Grid>
        </Grid>
        <Grid container direction="row" style={{ border: "1px solid white" }}>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Total Wages"
                name="totalwages"
                variant="filled"
                type="currency"
                value={payrundata.totalwages}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Total TAP"
                name="totaltap"
                variant="filled"
                type="currency"
                value={payrundata.totaltap}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Total SCP"
                name="totalscp"
                variant="filled"
                type="currency"
                value={payrundata.totalscp}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Total Allowances"
                name="totalallows"
                variant="filled"
                type="currency"
                value={payrundata.totalallows}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Total Deductions"
                name="totaldeducts"
                variant="filled"
                type="currency"
                value={payrundata.totaldeducts}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              <TextField
                label="Total Payroll"
                name="totalpayroll"
                variant="filled"
                type="currency"
                value={payrundata.totalpayroll}
                style={{ width: "100%" }}
                InputLabelProps={{
                  shrink: true,
                }}
                InputProps={{
                  readOnly: true,
                }}
              />
            </div>
          </Grid>
        </Grid>
      </form>
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  button: {
    margin: theme.spacing(1),
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "80vw",
    margin: "1rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },
  divider: {
    // Theme Color, or use css color in quote
    background: "white",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 350,
  },
  divContainer: {
    display: "flex",
    flexDirection: "row",
  },
}));

export default PaySummary;
