import React, { useState, useEffect } from "react";
import { Grid, TextField, Divider } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const initial_state = [
  {
    name: "",
    period: "",
    date: "",
    payrun: "",
    nett_pay: 0,
    bank_name: "",
    bank_acno: "",
    tap_acno: "",
    tap_amount: 0,
    scp_acno: "",
    scp_amount: 0,
    empid: "",
    status: "",
    allows_type1: "",
    allows_type1amt: 0,
    allows_type2: "",
    allows_type2amt: 0,
    allows_type3: "",
    allows_type3amt: 0,
    allows_type4: "",
    allows_type4amt: 0,
    allows_type5: "",
    allows_type5amt: 0,
    allows_type6: "",
    allows_type6amt: 0,
    allows_type7: "",
    allows_type7amt: 0,
    allows_type8: "",
    allows_type8amt: 0,
    deducts_type1: "",
    deducts_type1amt: 0,
    deducts_type2: "",
    deducts_type2amt: 0,
    deducts_type3: "",
    deducts_type3amt: 0,
    deducts_type4: "",
    deducts_type4amt: 0,
    deducts_type5: "",
    deducts_type5amt: 0,
    deducts_type6: "",
    deducts_type6amt: 0,
    deducts_type7: "",
    deducts_type7amt: 0,
    deducts_type8: "",
    deducts_type8amt: 0,
  },
];

const PayForm = ({ formdata, setFormdata, loadFormdata, setLoadFormdata }) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);

  useEffect(() => {
    setState({ ...formdata });
    setLoadFormdata(false);
    console.log("payform state", state);
  }, [loadFormdata]);

  const handleChange = (e) => {
    e.preventDefault();
    setState({ ...state, [e.target.name]: e.target.value });
  };

  console.log("payform", formdata);
  return (
    <form>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Allowances</h2>
        </Grid>

        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Deductions</h2>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type1"
                  variant="filled"
                  type="text"
                  value={state.allows_type1}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type2"
                  variant="filled"
                  type="text"
                  value={state.allows_type2}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type3"
                  variant="filled"
                  type="text"
                  value={state.allows_type3}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type4"
                  variant="filled"
                  type="text"
                  value={state.allows_type4}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type5"
                  variant="filled"
                  type="text"
                  value={state.allows_type5}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type6"
                  variant="filled"
                  type="text"
                  value={state.allows_type6}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type7"
                  variant="filled"
                  type="text"
                  value={state.allows_type7}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type8"
                  variant="filled"
                  type="text"
                  value={state.allows_type8}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
            </Grid>
            <Grid
              item
              sm={4}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Amount"
                  name="allows_type1amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type1amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="allows_type2amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type2amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>

              <div>
                <TextField
                  label="Amount"
                  name="allows_type3amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type3amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="allows_type4amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type4amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="allows_type5amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type5amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="allows_type6amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type6amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="allows_type7amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type7amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="allows_type8amt"
                  variant="filled"
                  type="number"
                  value={state.allows_type8amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
            </Grid>
          </Grid>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type1"
                  variant="filled"
                  type="text"
                  value={state.deducts_type1}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type2"
                  variant="filled"
                  type="text"
                  value={state.deducts_type2}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type3"
                  variant="filled"
                  type="text"
                  value={state.deducts_type3}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type4"
                  variant="filled"
                  type="text"
                  value={state.deducts_type4}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type5"
                  variant="filled"
                  type="text"
                  value={state.deducts_type5}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type6"
                  variant="filled"
                  type="text"
                  value={state.deducts_type6}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type7"
                  variant="filled"
                  type="text"
                  value={state.deducts_type7}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type8"
                  variant="filled"
                  type="text"
                  value={state.deducts_type8}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
            </Grid>
            <Grid
              item
              sm={4}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type1amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type1amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type2amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type2amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type3amt"
                  variant="filled"
                  type="number"
                  value={formdata.deducts_type3amt}
                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type4amt"
                  variant="filled"
                  type="number"
                  value={formdata.deducts_type4amt}
                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type5amt"
                  variant="filled"
                  type="number"
                  value={formdata.deducts_type5amt}
                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type6amt"
                  variant="filled"
                  type="number"
                  value={formdata.deducts_type6amt}
                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type7amt"
                  variant="filled"
                  type="number"
                  value={formdata.deducts_type7amt}
                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type8amt"
                  variant="filled"
                  type="number"
                  value={formdata.deducts_type8amt}
                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
            </Grid>
          </Grid>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div>
            <h2>{formdata.name}</h2>
          </div>
          <Divider variant="fullWidth" className={classes.divider} />
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="number"
              value={formdata.wages}
              onChange={setFormdata}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Allowances"
              name="allowances"
              variant="filled"
              type="number"
              value={formdata.allowances}
              onChange={setFormdata}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Deductions"
              name="deductions"
              variant="filled"
              type="number"
              value={formdata.deductions}
              onChange={setFormdata}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nettpay"
              variant="filled"
              type="number"
              value={formdata.nettpay}
              onChange={setFormdata}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

