import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Paper from "@material-ui/core/Paper";
import { selector } from "recoil";
import { useRecoilValue, useSetRecoilState, useRecoilState } from "recoil";
import { departmentsSelector } from "../helpers/Recoilhelpers";
import { departments_url } from "../utils/constants";

const drawerWidth = 240;

const fetchDeptDetails = selector({
  key: "deptDetailsSelector",
  get: async ({ get }) => {
    try {
      const res = await fetch(departments_url);
      const data = await res.json();
      return data;
    } catch (error) {
      throw error;
    }
  },
});

const DetailsWithSuspense = () => {
  const deptDetails = useRecoilValue(fetchDeptDetails);
  const { data } = deptDetails;

  return data.map((item) => {
    <div key={item.id}>
      <p>{item.name}</p>
    </div>;
  });
};

const Example = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const departsdata = useRecoilValue(departmentsSelector);

  return (
    <React.Suspense fallback={<div>Loading...</div>}>
      <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
        <h2>Departments</h2>
        <DetailsWithSuspense />
      </Paper>
    </React.Suspense>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
}));

export default Example;
