                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type7"
                  variant="filled"
                  type="text"
                  value={formdata.allows_type7}
                  onChange={setFormdata}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type8"
                  variant="filled"
