          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Deduction"
                  name="deducts_type1"
                  variant="filled"
                  type="text"
                  value={formdata.deducts_type1}
