                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type6amt"
                  variant="filled"
                  type="number"
