                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type7amt"
                  variant="filled"
                  type="number"
                  value={formdata.deducts_type7amt}
                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type8amt"
                  variant="filled"
                  type="number"
                  value={formdata.deducts_type8amt}
                  onChange={(e) => setFormdata(e)}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
            </Grid>
          </Grid>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div>
            <h2>{formdata.name}</h2>
          </div>
          <Divider variant="fullWidth" className={classes.divider} />
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="number"
              value={formdata.wages}
              onChange={setFormdata}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Allowances"
              name="allowances"
              variant="filled"
              type="number"
              value={formdata.allowances}
              onChange={setFormdata}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Deductions"
              name="deductions"
              variant="filled"
              type="number"
              value={formdata.deductions}
              onChange={setFormdata}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nettpay"
              variant="filled"
              type="number"
              value={formdata.nettpay}
              onChange={setFormdata}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

