import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import MaterialTable from "material-table";
import EditableList from "react-list-editable";
import "react-list-editable/lib/react-list-editable.css";
import clsx from "clsx";
import axios from "axios";
import { useAsync } from "react-async";
import { Paper, Grid, Divider, TextField, MenuItem, ListSubheader } from "@material-ui/core";
import { employees_url } from "../utils/constants";
import { useEmployeesContext } from "../context/employees_context";
import { usePayslipsContext } from "../context/payslips_context";
import { useTablesContext } from "../context/tables_context";

const drawerWidth = 240;
const url = "https://course-api.com/react-tabs-project";

// const loadEmp = async () =>
//   await fetch(employees_url)
//     .then((res) => (res.ok ? res : Promise.reject(res)))
//     .then((res) => res.json());

// const loadEmp = async () => {
//   const { data } = await axios.get(employees_url);
//   return data;
// };

const paydata_initial = {
  name: "",
  wages: 0,
  allowances: 0,
  deductions: 0,
  netpay: 0,
};

const payitemsdata_initial = [
  { id: 1, item: "Basic Salary", amt: 0 },
  { id: 2, item: "", amt: 0 },
  { id: 3, item: "", amt: 0 },
  { id: 4, item: "", amt: 0 },
  { id: 5, item: "", amt: 0 },
  { id: 6, item: "", amt: 0 },
  { id: 7, item: "", amt: 0 },
  { id: 8, item: "", amt: 0 },
  { id: 9, item: "", amt: 0 },
  { id: 10, item: "", amt: 0 },
];

const Payrunbatch = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const { loadEmployees, employees, employees_loading } = useEmployeesContext();
  const { getPayslipitems, payslipitems, payslipitems_loading } =
    usePayslipsContext();
  const { loadPayitems, payitems, payitems_loading } = useTablesContext();
  const [paydata, setPaydata] = useState(paydata_initial);
  const [payitemsdata, setPayitemsdata] = useState(payitemsdata_initial);
  const [loading, setLoading] = useState(true);
  const [jobs, setJobs] = useState([]);
  const [value, setValue] = useState(0);

  const handleClick = () => {};

  const handleButtonClick = (index) => {
    console.log();
  };

  useEffect(() => {
    loadEmployees();
    loadPayitems();
  }, []);

  const onListChange = (newList) => {
    setPayitemsdata(newList);
    console.log("list", payitemsdata, newList);
  };

  if (employees_loading) {
    <div>
      <h2>Loading...employees</h2>
    </div>;
  }
  if (payitems_loading) {
    <div>
      <h2>Loading...payitems</h2>
    </div>;
  }

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
        <Grid
          direction="row"
          container
          spacing={1}
          style={{ border: "1px solid white" }}
        >
          <Grid
            container
            item
            sm={2}
            direction="column"
            align="left"
            style={{ border: "1px solid white" }}
          >
            {employees.map((item, index) => {
              console.log("item", item, index);
              return (
                <button
                  key={index}
                  onClick={(e) => {
                    setValue(index);
                    handleButtonClick(index);
                  }}
                  className={`classes.jobbtn`}
                  // ${index === value && "activebtn"} `}
                >
                  <h3>{item.name}</h3>
                </button>
              );
            })}
          </Grid>

          <Grid
            container
            item
            sm={3}
            align="left"
            style={{ border: "1px solid white" }}
          >
            <form>
              <ul>
                {payitemsdata.map((row, index) => {
                  return (
                    <TextField
                      label="pay item"
                      variant="filled"
                      type="text"
                      style={{ width: 200 }}
                      value={row.item}
                      select
                    >
                      <ListSubheader>Wages</ListSubheader>
                      {payitems.filter(function(item){return}).map((row) => {
                        return (
                          <MenuItem key={row.id} value={row.name}>
                            {row.name}
                          </MenuItem>
                        );
                      })}
                    </TextField>
                  );
                })}
              </ul>
            </form>
          </Grid>
          <Grid
            container
            item
            sm={2}
            //align="right"
            style={{ border: "1px solid white" }}
          >
            <ul>
              {payitemsdata.map((row, index) => {
                return (
                  <TextField
                    label="amount"
                    variant="filled"
                    type="currency"
                    style={{ width: 100 }}
                    value={row.amt}
                  />
                );
              })}
            </ul>
          </Grid>
          <Grid container item sm={4} style={{ border: "1px solid white" }}>
            <h2>Pay Details</h2>
          </Grid>
        </Grid>
      </section>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "70vw",
    margin: "5rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },
}));

export default Payrunbatch;
