import React, { useEffect } from "react";
import MaterialTable from "material-table";
import { makeStyles } from "@material-ui/core/styles";
import { useHistory, Link } from "react-router-dom";

import { useTablesContext } from "../context/tables_context";

const columns = [
  {
    title: "Name",
    field: "name",
  },
  {
    title: "Relationship",
    field: "relationship",
  },
  {
    title: "BIrth Date",
    field: "birth_date",
    type: "date",
  },
  {
    title: "Phone",
    field: "phone",
  },
  { title: "Age", field: "age", type: "numeric" },
];

export default function UpdateBatchEducations({ linkid }) {
  let history = useHistory();
  const classes = useStyles();
  const {
    loadSingleBatchEducation,
    singlebatcheducation,
    addEducation,
    deleteEducation,
    updateEducation,
    singlebatch_education_loading,
    singlebatch_education_error,
  } = useTablesContext();

  useEffect(() => {
    //console.log(linkid);
    loadSingleBatchEducation(linkid);
  }, []);

  const update_Education = (data) => {
    updateEducation({ id: data.id, ...data });
    loadSingleBatchEducation();
  };

  const add_Education = (data, linkid) => {
    addEducation({ ...data, linkid: linkid });
    loadSingleBatchEducation();
  };

  const delete_Education = (data) => {
    const { id } = data;
    deleteEducation(id);
    loadSingleBatchEducation();
  };

  if (singlebatch_education_loading) {
    return <div>Loading...Education</div>;
  }
  return (
    <div className={classes.root}>
      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={singlebatcheducation}
          title="Education"
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  add_Education(newData, linkid);
                  resolve();
                }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  update_Education(newData);
                  resolve();
                }, 1000);
              }),
            onRowDelete: (oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  delete_Education(oldData);
                  resolve();
                }, 1000);
              }),
          }}
          options={{
            filtering: true,
            headerStyle: {
              backgroundColor: "#01579b",
              color: "#FFF",
            },
            showTitle: true,
          }}
        />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
