import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import { TextField, Grid, Paper, Container, Box } from "@material-ui/core";
import CardLayout from "../helpers/CardLayout";
import CardLayout2 from "../helpers/CardLayout2";
import CardLayout3 from "../helpers/CardLayout3";
import Copyright from "../components/Copyright";
import { CustomDialog } from "../helpers/CustomDialog";

import { useLeavesContext } from "../context/leaves_context";
import { useExpensesContext } from "../context/expenses_context";
import { usePayslipsContext } from "../context/payslips_context";
import LeaveTableView from "./LeaveTableView";
import LeaveTableAdmin from "./LeaveTableAdmin";
import ExpenseTableView from "./ExpenseTableView";
import ExpenseTableAdmin from "./ExpenseTableAdmin";
import PayslipTableView from "./PayslipTableView";
import PayslipTableAdmin from "./PayslipTableAdmin";

const drawerWidth = 240;

const FILTERSTRING = "Pending";

const EmployeeView = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  const [leavesdata, setLeavesdata] = useState([]);
  const [isLeaveDialogOpen, setIsLeaveDialogOpen] = useState(false);
  const [expensesdata, setExpensesdata] = useState([]);
  const [isExpenseDialogOpen, setIsExpenseDialogOpen] = useState(false);
  const [payslipsdata, setPayslipsdata] = useState([]);
  const [isPayslipDialogOpen, setIsPayslipDialogOpen] = useState(false);

  const {
    leaves,
    editLeaveID,
    leaves_loading,
    deleteLeave,
    loadPendingLeaves,
    getSingleLeave,
    setEditLeaveID,
    setIsLeaveEditingOn,
    setIsLeaveEditingOff,
    resetSingleLeave,
  } = useLeavesContext();

  const {
    expenses,
      loadPendingExpenses,
     } = useExpensesContext();

    const {
      isPayslipEditing,
      single_payslip,
      updatePayslip,
      addPayslip,
      editPayslipID,
      loadPayslips,
      getSinglePayslip,
      single_payslip_loading,
      single_payslip_error,
      payslip_period,
      payslip_earning_amount,
      payslip_deduction_amount,
      setPayslipEarningAmount,
      setPayslipDeductionAmount,
      payslipearnings,
      payslipdeductions,
      getSingleBatchPayslipEarnings,
      getSingleBatchPayslipDeductions,
    } = usePayslipsContext();

  const handleLeaveDialogOpen = () => {
    setLeavesdata([]);
    setLeavesdata([...leaves]);
    setIsLeaveDialogOpen(true);
  };

  const handleLeaveDialogClose = () => {
    setIsLeaveDialogOpen(false);
    loadPendingLeaves(FILTERSTRING);
  };

  const handleExpenseDialogOpen = () => {
    setExpensesdata([]);
    setExpensesdata([...expenses]);
    setIsExpenseDialogOpen(true);
  };

  const handleExpenseDialogClose = () => {
    setIsExpenseDialogOpen(false);
    loadPendingExpenses(FILTERSTRING);
  };

  const handlePayslipDialogOpen = () => {
    setPayslipsdata([]);
    setPayslipsdata([...payslips]);
    setIsPayslipDialogOpen(true);
  };

  const handlePayslipDialogClose = () => {
    setIsPayslipDialogOpen(false);
    loadPendingPayslips(FILTERSTRING);
  };

  return (
    <div>
      <div className={classes.appBarSpacer} />
      <div style={{ paddingLeft: 50 }}>
        <h2>Welcome Admin!</h2>
        <h3>Dashboard</h3>
      </div>
      <Container maxWidth="lg" className={classes.container}>
        <Grid container spacing={3}>
          {/* Chart */}
          <Grid item xs={12} md={8} lg={12}>
            {/* <Paper className={fixedHeightPaper}>
              <div>Chart</div>
            </Paper> */}
            <CardLayout3
              title="Chart"
              // handleClick={handleEarningsDialogOpen}
            >
              <h2>Chart</h2>
              {/* <Payslip_ViewEarnings /> */}
            </CardLayout3>
          </Grid>
          {/* Recent Deposits */}
          <Grid item xs={6} md={8} lg={6}>
            <CardLayout3
              title="Payroll pending for approval"
              handleClick={handlePayrollDialogOpen}
            >
              <PayslipTableView />
            </CardLayout3>
          </Grid>
          <Grid item xs={6} md={8} lg={6}>
            <CardLayout3
              title="Site Allowances pending for approval"
              // handleClick={handleLeaveDialogOpen}
            >
              <h2>Site allowances</h2>
            </CardLayout3>
          </Grid>
          <Grid item xs={6} md={8} lg={6}>
            <CardLayout3
              title="Leaves pending for approval"
              handleClick={handleLeaveDialogOpen}
            >
              <LeaveTableView />
            </CardLayout3>
          </Grid>
          {/* Recent Orders */}
          <Grid item xs={6} md={8} lg={6}>
            <CardLayout3
              title="Expenses pending for approval"
              handleClick={handleExpenseDialogOpen}
            >
              <ExpenseTableView />
            </CardLayout3>
          </Grid>
        </Grid>
        <Box pt={4}>
          <Copyright />
        </Box>
        <CustomDialog
          isOpen={isLeaveDialogOpen}
          handleClose={handleLeaveDialogClose}
          title=""
          showButton={true}
          isFullscreen={false}
          isFullwidth={false}
        >
          <LeaveTableAdmin
            setLeavesdata={setLeavesdata}
            leavesdata={leavesdata}
            handleDialogClose={handleLeaveDialogClose}
          />
        </CustomDialog>
        <CustomDialog
          isOpen={isExpenseDialogOpen}
          handleClose={handleExpenseDialogClose}
          title=""
          showButton={true}
          isFullscreen={false}
          isFullwidth={false}
        >
          <ExpenseTableAdmin
            setExpensesdata={setExpensesdata}
            expensesdata={expensesdata}
            handleDialogClose={handleExpenseDialogClose}
          />
        </CustomDialog>
        <CustomDialog
          isOpen={isPayslipDialogOpen}
          handleClose={handlePayslipDialogClose}
          title=""
          showButton={true}
          isFullscreen={false}
          isFullwidth={false}
        >
          <PayslipTableAdmin
            setpayslipsdata={setPayslipsdata}
            payslipsdata={payslipsdata}
            handleDialogClose={handlePayslipDialogClose}
          />
        </CustomDialog>
      </Container>
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0 8px",
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: "none",
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: "relative",
    whiteSpace: "nowrap",
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: "hidden",
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up("sm")]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: "100vh",
    overflow: "auto",
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
  },
  fixedHeight: {
    height: 240,
  },
}));

export default EmployeeView;
