import React, { useState, useReducer, useEffect } from "react";
import {
  Button,
  Icon,
  TextField,
  Paper,
  Typography,
  Select,
  Divider,
  Grid,
  Toolbar,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import { useEmployeesContext } from "../context/employees_context";
import { usePayslipsContext } from "../context/payslips_context";
import { Controller, useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";

import Payslip_Earnings from "./Payslip_Earnings";
import Payslip_Deductions from "./Payslip_Deductions";

const initial_values = {
  name: "",
  period: "",
  date: "",
  bank_name: "",
  bank_acno: "",
  status: "Pending",
  total_earnings: 0,
  total_deductions: 0,
  tap_amount: 0,
  scp_amount: 0,
  basic_pay: 0,
  nett_pay: 0,
};

const PayslipForm = () => {
  let history = useHistory();
  const classes = useStyles();
  const {
    isPayslipEditing,
    single_payslip,
    updatePayslip,
    addPayslip,
    editPayslipID,
    loadPayslips,
    single_payslip_loading,
    payslip_period,
  } = usePayslipsContext();
  const { loadEmployees, employees } = useEmployeesContext();

  const { handleSubmit, control } = useForm();
  const {
    name,
    period,
    date,
    bank_name,
    bank_acno,
    status,
    total_earnings,
    total_deductions,
    tap_amount,
    scp_amount,
    basic_pay,
    nett_pay,
  } = single_payslip || initial_values;
  useEffect(() => {
    loadEmployees();
  }, []);

  const onSubmit = (data) => {
    //   if (isPayslipEditing) {
    //     updatePayslip({ id: editPayslipID, ...data });
    //   } else {
    //     addPayslip({ ...data });
    //   }
    //   loadPayslips();
    //   history.push("/batchpayslips");
  };

  if (single_payslip_loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Paper className={classes.root}>
        <Typography variant="h5" component="h3">
          PAYSLIP FORM
        </Typography>
        <Typography component="p">Payslip Preparation</Typography>

        <form onSubmit={handleSubmit(onSubmit)}>
          <div>
            <Controller
              name="name"
              control={control}
              defaultValue={name}
              render={({
                field: { onChange, value },
                fieldState: { error },
              }) => {
                return (
                  <TextField
                    label="Name"
                    id="margin-normal"
                    name="name"
                    defaultValue={name}
                    className={classes.textField}
                    onChange={onChange}
                    error={!!error}
                    helperText={error ? error.message : null}
                    select
                  >
                    {employees.map((e) => {
                      return (
                        <MenuItem key={e.name} value={e.name}>
                          {e.name}
                        </MenuItem>
                      );
                    })}
                  </TextField>
                );
              }}
              rules={{ required: "Name required" }}
            />
          </div>
          <div>
            <Controller
              name="period"
              control={control}
              defaultValue={period}
              render={({
                field: { onChange, value },
                fieldState: { error },
              }) => {
                return (
                  <TextField
                    label="Period"
                    id="margin-normal"
                    name="period"
                    defaultValue={period}
                    className={classes.textField}
                    onChange={onChange}
                    error={!!error}
                    helperText={error ? error.message : null}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                );
              }}
              rules={{ required: "Period is required" }}
            />

            <Controller
              name="date"
              control={control}
              defaultValue={date}
              render={({
                field: { onChange, value },
                fieldState: { error },
              }) => {
                return (
                  <TextField
                    label="Date"
                    type="date"
                    id="margin-normal"
                    name="date"
                    defaultValue={date}
                    className={classes.textField}
                    onChange={onChange}
                    error={!!error}
                    helperText={error ? error.message : null}
                    InputLabelProps={{
                      shrink: true,
                    }}
                  />
                );
              }}
              rules={{ required: "Date is required" }}
            />
          </div>
          <div>
            <Controller
              name="bank_name"
              control={control}
              defaultValue={bank_name}
              render={({
                field: { onChange, value },
                fieldState: { error },
              }) => {
                return (
                  <TextField
                    label="Bank Name"
                    id="margin-normal"
                    name="bank_name"
                    defaultValue={bank_name}
                    className={classes.textField}
                    onChange={onChange}
                    error={!!error}
                    helperText={error ? error.message : null}
                  />
                );
              }}
              // rules={{ required: "Reason is required" }}
            />

            <Controller
              name="bank_acno"
              control={control}
              defaultValue={bank_acno}
              render={({
                field: { onChange, value },
                fieldState: { error },
              }) => {
                return (
                  <TextField
                    label="Bank AC No"
                    id="margin-normal"
                    name="bank_acno"
                    defaultValue={bank_acno}
                    className={classes.textField}
