import React, { useState } from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

// import Chart from "./Chart";
// import Deposits from "./Deposits";
// import Orders from "./Orders";

import Appbanner from "./Appbanner";
import SideDrawer from "./SideDrawer";
import SingleEmployee from "./SingleEmployee";
import SingleLeave from "./SingleLeave";
import SingleExpense from "./SingleExpense";
import SingleDailyAllowance from "./SingleDailyAllowance";
import SingleDailyAllowsDetls from "./SingleDailyAllowsDetls";
import BatchDailyAllowances from "./BatchDailyAllowances";
import SinglePayslip from "./SinglePayslip";
import Login from "./Login";

import {
  Home,
  Dashboard,
  AllEmployees,
  Leave,
  Expenses,
  Payslip,
  BatchPayslips,
  Payroll,
  Departments,
  Designation,
  Tables,
  Clients,
  Allowances,
  DailyAllowances,
  PrivateRoute,
  Error,
} from "../pages";

const drawerWidth = 240;

export default function DashboardMain() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(true);
  const [login, setLogin] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleLogin = () => {
    setLogin(true);
  };

  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  if (!login) {
    return <Login setLogin={setLogin} />;
  }
  return (
    <div className={classes.root}>
      <CssBaseline />
      <Router>
        <Appbanner
          handleDrawerOpen={handleDrawerOpen}
          handleDrawerClose={handleDrawerClose}
          open={open}
          setLogin
          title="Human Resource Management System - AppSmiths"
        />

        <SideDrawer
          handleDrawerOpen={handleDrawerOpen}
          handleDrawerClose={handleDrawerClose}
          open={open}
        />

        <main className={classes.content}>
          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            {/* <Route exact path="/home">
              <Home />
            </Route> */}
            <Route exact path="/allemployees">
              <AllEmployees />
            </Route>
            <Route exact path="/payroll">
              <Payroll />
            </Route>
            <Route exact path="/payslip">
              <Payslip />
            </Route>
            <Route exact path="/singlepayslip">
              <SinglePayslip />
            </Route>
            <Route exact path="/batchpayslips">
              <BatchPayslips />
            </Route>
            <Route exact path="/leave">
              <Leave />
            </Route>
            <Route exact path="/dailyallowances">
              <DailyAllowances />
            </Route>
            <Route exact path="/singledailyallowsdetlstable">
              <SingleDailyAllowsDetls />
            </Route>
            <Route exact path="/expenses">
              <Expenses />
            </Route>
            <Route exact path="/departments">
              <Departments />
            </Route>
            <Route exact path="/designation">
              <Designation />
            </Route>
            <Route exact path="/tables">
              <Tables />
            </Route>
            <Route exact path="/allowances">
              <Allowances />
            </Route>
            <Route exact path="/clients">
              <Clients />
            </Route>
            <Route exact path="/singleemployee">
              <SingleEmployee />
            </Route>
            <Route exact path="/singleleave">
              <SingleLeave />
            </Route>
            <Route exact path="/singleexpense">
              <SingleExpense />
            </Route>
            <Route exact path="/singledailyallowance">
              <SingleDailyAllowance />
            </Route>
            <Route exact path="/batchdailyallowances">
              <BatchDailyAllowances />
            </Route>
            <Route exact path="/error">
              <Error />
            </Route>
            {/* <Route
              exact
              path="/employees/:empId"
              children={<SingleEmployee />}
            /> */}
            <Route path="*">
              <Error />
            </Route>
          </Switch>
        </main>
      </Router>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0 8px",
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: "none",
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: "relative",
    whiteSpace: "nowrap",
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: "hidden",
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up("sm")]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: "100vh",
    overflow: "auto",
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
  },
  fixedHeight: {
    height: 240,
  },
}));
