import React, { useState, useEffect } from "react";

import MaterialTable from "material-table";
import { makeStyles } from "@material-ui/core/styles";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import CheckIcon from "@material-ui/icons/Check";
import SearchIcon from "@material-ui/icons/Search";
import { useHistory, Link } from "react-router-dom";

import { usePayslipsContext } from "../context/payslips_context";

const columns = [
  {
    title: "Name",
    field: "name",
  },
  { title: "Amount", field: "amount", type: "numeric" },
];

export default function Payslip_Earnings(empid) {
  let history = useHistory();
  const classes = useStyles();
  const {
    payslipearnings,
    payslip_earning_amount,
    setPayslipEarningAmount,
    loadPayslipEarnings,
    addPayslipEarning,
    updatePayslipEarning,
    deletePayslipEarning,
    updatePayslip,
    editPayslipID,
    single_payslip,
  } = usePayslipsContext();
  const [data, setData] = useState([
    { name: "Housing Allowances", amount: 250 },
  ]);

  const calc_Earning = (data) => {
    const sum = data.reduce((a, v) => (a = a + v.amount), 0);
    setPayslipEarningAmount(sum);
  };

  useEffect(() => {
    loadPayslipEarnings(empid);
  }, []);

  const update_payslip = () => {
    updatePayslip({
      id: editPayslipID,
      total_earnings: payslip_earning_amount,
      ...paydata,
    });
  }

  const update_PayslipEarning = async (data) => {
    const { id, empid, ...fields } = data;
    const { rec_id, total_earnings, ...paydata } = single_payslip;
    console.log(fields);
    updatePayslipEarning({ id: data.id, ...fields });
    updatePayslip()
    loadPayslipEarnings(empid);
  };

  const add_PayslipEarning = (data, empid) => {
    console.log(data, empid);
    addPayslipEarning({ ...data, empid: empid });
    loadPayslipEarnings(empid);
  };

  const delete_PayslipEarning = (data) => {
    const { id } = data;
    deletePayslipEarning(id);
    loadPayslipEarnings(empid);
  };

  return (
    <div className={classes.root}>
      {/* <h1>Expenses Claims Application</h1> */}

      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={data}
          title="Income"
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  add_PayslipEarning(newData, empid);

                  resolve();
                }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  const dataUpdate = [...data];
                  const index = oldData.tableData.id;
                  dataUpdate[index] = newData;

                  update_PayslipEarning(dataUpdate);
                  calc_Earning(dataUpdate);

                  resolve();
                }, 1000);
              }),
            onRowDelete: (oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  delete_PayslipEarning(oldData);

                  resolve();
                }, 1000);
              }),
          }}
        />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
